insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11410, 'Sandook', False, True, False, False, 2015, 6.5, 'Sandook is a Marathi movie starring Sumeet Raghvan and Bharagavi Chirmuley in lead roles. it is a satirical comic thriller set in pre independence era in a small town. The main protagonist Wamanrao Ashtaputre is a simple honest man who always dreams of getting rid of the British Raj. The film is filled with hilarious moments and one liners. Sandook is a complete family entertainer with a feel good factor throughout the story', 'tt4635162', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11414, 'My Last Year With The Nuns', False, True, False, False, 2014, 7.6, 'Master monologist Matt Smith turns his 8th grade year in 1966-67 into a wild coming-of-age film---a comedy grounded in his Catholic education, racism, and the joys and horrors that are male adolescence.', 'tt3089126', '/h0NuKJtZJzfhW5n8x5LEZ8kHVEU.jpg');
