insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11417, 'Lone Wolves', False, True, False, False, 2016, 2, 'After an apocalypse of questionable origin, Private James Conroy is living in an abandoned shack at the base of a radio tower. When Conroy finally contacts Garry Freeman, marooned in a space station, they have to learn to work together if they''re going to save themselves, and maybe all of civilization.', 'tt4982252', '/ygqDEUCSa8tjW0lzOxJNXB9Ramn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11427, 'Duel of Champions', False, True, False, False, 1961, 5.2, 'A Roman nobleman, Horatius leads an imperial legion during the long and bloody war between the Romans and the Albans.  A desperate arrangement is agreed on how to settle the war.  Three valiant brothers are chosen from each side to fight one last fierce and bloody duel...', 'tt0055264', '/60oNGntte7mT3lqPQnKRA6ehFJa.jpg');
