insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11451, 'Brothers on the Line', False, True, False, False, 2012, 7.9, 'Brothers on the Line explores the extraordinary journey of the Reuther brothers – Walter, Roy, and Victor – union organizers whose unshakeable devotion led an army of workers into an epic human rights struggle.', 'tt1344811', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11453, 'Ninja Death', False, True, False, False, 1987, 5.2, 'Tiger is the owner of a brothel who moonlights as a kung fu artist under the watchful eye of "Master." They run into serious trouble when ninjas inexplicably show up and jeopardize Tiger''s prostitution ring.', 'tt0199817', '/10orlkfW8uZ0NLEGVn1C8HGT4VV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11457, 'FLicKeR', False, True, False, False, 2009, 6.1, 'In 1960, Brion Gysin invented the Dream Machine, a hypnotic light device with the power to induce hallucinations, drugless highs, and revolutionize human consciousness. It looks simple enough; a 100-watt light bulb, a motor, and a rotating cylinder with cutouts. Just sit in front of it, close your eyes, and wait for the visions to come. The Dream Machine enthralled mystics and freethinkers everywhere; Kurt Cobain had a dream machine, and William S. Burroughs thought it could be used to “storm the citadels of enlightenment.” With a custom-made Dream Machine in tow, director Nik Sheehan takes us on a journey into the life of Brion Gysin; his art, his complex ideas, and his friendships with some of the most eccentric counter-cultural icons. Taking the Dream Machine as the basis of its explorations, FLicKeR asks crucial questions about the nature of art and consciousness, and imagines a humanity liberated to explore its creativity in complete freedom.', 'tt1236194', '/zjphH7UipHTCTR48NNfWEoIDVOW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11461, 'Moving Target', False, True, False, False, 1988, 5.8, 'A bounty hunter with a pregnant wife who is urging him to get a regular job gets framed for murder and trapped in a war between Russian gangsters.', 'tt0117096', '/gVittqtNxfzAZLBjYEfBb27Drmu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11470, 'Summer School Teachers', False, True, False, False, 1974, 4.7, 'A trio of naive, but eager young Midwestern women go to California to teach summer school classes at Regency High School: Perky and willful Conklin T. starts up and coaches an all-female football team, stuffy chemistry teacher Sally Hanson manages to loosen up after she falls hard for a surly juvenile delinquent student, and pert and liberated photography instructor Denise Carter becomes involved with both a two-faced male chauvinist jerk and a more decent and understanding guy.', 'tt0073764', '/ay4rZZMccTonal3KYlDc5IesT1v.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11474, 'Stash House', False, True, False, False, 2012, 4.8, 'Dave and Emma have found the perfect house, until they discover a stash of heroine and end up imprisoned in it by violent thugs.', 'tt1919090', '/kAmiwUbvMdrAQRYJOKKAMF1Twm9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11476, 'The Portal', False, True, False, False, 2010, 3.6, 'A dimension-traveling wizard gets stuck in the 21st century because cell-phone radiation interferes with his magic. With his home world on the brink of war, he seeks help from a jaded travel agent who he mistakes for a great sorceress. Without his powers to prove his identity, she has trouble taking him seriously, but finally agrees to reveal the secrets of our world in exchange for a lunch date.', 'tt3009714', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11478, 'First Man Into Space', False, True, False, False, 1959, 5.5, 'Once he was a handsome, fearless test pilot. ow he is a viscious, hideous love-killer. Did the deadly gamma rays do it? In this classic 50s sci-fi thriller, cocky all-american test pilot Dan Prescott disobeys orders and flies an experimental rocket 250 miles above the earth, thus becoming the first man into space.', 'tt0052805', '/lKBIGRetSbZ6cBRWqaNzwHvkgy9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11483, 'Mickey', False, True, False, False, 1948, 7, 'Mickey, an orphan who has been brought up in a mining settlement, is sent to New York to live with her aunt.', 'tt0009369', '/tB7XlpNbGMBD7XrnXkbLmTWvnpJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11484, 'Plan 9', False, True, False, False, 2015, 4.4, 'Plan 9 is the story of Nilbog, a small town with a big story. The beginning of an invasion! However, instead of lasers, space ships, and epic force, these aliens have a different plan for the inhabitants of Earth. To resurrect their dead as their own army set with but one goal… To wipe out all mankind! Only the townsfolk on this Halloween night stand in the way of total domination. From the police department, to those trapped in a convenient store, and even those trying to stay alive in the streets, this night will decide the fates of all who walk the planet and thought they were the top of the food chain.', 'tt2014319', '/h1S1YfbKmjbgckdj6uXIkz5rENJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11488, 'Sensation', False, True, False, False, 2011, 6.1, 'Man-boy farmer Donal discovers that his father has suddenly passed away. Now on his own, Donal attempts to lose his virginity by hiring a prostitute to help him get out of his rut. And so he meets Kim, an aging Kiwi escort. After engaging in a strictly sexual encounter, their relationship soon progresses into a lusty romance, then to a business enterprise between two partners. Through these exploits, Donal and Kim provide each other with a sense of love and belonging that neither experienced beforehand.', 'tt1451632', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11493, 'Alter Egos', False, True, False, False, 2012, 5.2, 'At a time when superheroes have lost government funding and public support, a superhero meets a girl who can help him overcome his own emotional crisis.', 'tt1907614', '/t7vc8jByx9hXMzCLqMAtsapp3cG.jpg');
