insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11503, 'Witchcraft', False, True, False, False, 1989, 3.2, 'A new mother and her child move into her mother-in-law''s dark old mansion. She soon begns to suspect that neither the house nor her mother-in-law are quite what they seem to be.', 'tt0245567', '/6W8rdTifkRBk26rNd5dTegJlLmJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11526, 'Mr. Wong in Chinatown', False, True, False, False, 1939, 6.1, 'A pretty Chinese woman, seeking help from San Francisco detective James Lee Wong, is killed by a poisoned dart in his front hall, having time only to scrawl "Captain J" on a sheet of paper. She proves to be Princess Lin Hwa, on a secret military mission for Chinese forces fighting the Japanese invasion. Mr. Wong finds two captains with the intial J in the case, neither being quite what he seems; there''s fog on the waterfront and someone still has that poison-dart gun...', 'tt0031680', '/eG3bPkQBKmvRzAz3PO8FE5tB39p.jpg');
