insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11528, 'Barabbas', False, True, False, False, 2013, 5.4, 'Barabbas or Jesus Barabbas (literally "son of the father" or "Jesus, son of the father" respectively) is a figure in the account of the Passion of Christ, in which he is the insurrectionary whom Pontius Pilate freed at the Passover feast in Jerusalem, instead of Jesus Christ.', 'tt2535664', '/wCaCLrODsQwFyunqKRfXHugMzGA.jpg');
