insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11646, 'Vasermil', False, True, False, False, 2007, 6.7, 'Vasermil tells the story of three teenagers who live in the same tough neighborhood, growing up in an unforgiving environment, pinning their hopes on football as a way out. Shlomi lives with his widowed mother, little sister, and step-father and works as a pizza delivery boy. Adiel, of Ethiopian descent, has to look after his young brother and sick mother. Dima, a new immigrant from Russia, has a father who is unemployed and a mother who works as a cleaning lady. The three teens are recruited by the coach of the local football team. Learning to work together as a team is the key to success in the tournament and success in the tournament means getting noticed by the scouts of the local football empire. In order to win the tournament they will have to play as a team, overcome their differences, get over their sense of inferiority and prejudice.', 'tt1124001', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11647, 'Campus Rhythm', False, True, False, False, 1943, 5.5, 'Radio singer Joan Abbott, known as the "Crunchy-Wunchy Thrush", does not want to renew her contract with the cereal sponsor, as she wants to go to college. But her guardian, her Uncle Willie signs the contract in order to pay off his own debts.  But this time Joan won''t take no for an answer and enrolls under an assumed name.  When Joan goes missing, the radio institutes a search for Joan via a publicity stunt.', 'tt0035710', '/j7IriWcw3pZYWMUZ3wbaFrZxeyZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (11649, 'David and Goliath', False, True, False, False, 1960, 4.9, 'The big-budget, epic film on young King David who took on the mighty warrior Goliath against all odds.', 'tt3962210', '/a8ZE7AJO1UchOGCLe2nLR61HOpN.jpg');
