insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12348, 'The Castilian', False, True, False, False, 1963, 5, 'Also released as Valley of the Swords, this lugubrious US/Spanish co-production features the usual mid-1960s "tax shelter" international cast. Broderick Crawford plays a despotic 10th century Spanish king who, in cahoots with the invading Moors, has banished handsome Castilian nobleman Spartaco Santoni. With the surreptitious aid of Crawford''s daughter Teresa Velasquez, Santoni assembles an army to march against the Moors. In keeping with the 13th century epic poem from which this film was derived ("El Poema de Fernan Gonzales") Santoni''s path is smoothed by the celestial intervention of patron saints Milan and Santiago. Among the big names picking up a few tax-free dollars in The Castilian are Cesar Romero, Linda Darnell, Alida Valli and Fernando Rey.', 'tt0056646', '/aIOHeoWMRfYnR3PpJ7tFmLxBUsV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12349, 'Too Scared to Scream', False, True, False, False, 1985, 5, 'A killer of young women is loose in an exclusive apartment complex and a detective is out to stop him. The chief suspect is a Shakespeare-spouting doorman who lives with his invalid mother. Meanwhile, an undercover cop moves into the apartment house. This move is complicated by her relationship with the detective.', 'tt0090186', '/6TX5DCYJGAkh4PQMPB98c8PB7TX.jpg');
