insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12363, 'Jack Frost 2: Revenge of the Mutant Killer Snowman', False, True, False, False, 2000, 3.7, 'The sheriff and his deputies from the first movie decide to take a vacation in the Caribbean. Their holiday will be short-lived, however, as the thawed murderer gets inadvertently re-frozen and brought back to life. As if that weren''t bad enough, he now has the ability to remain frozen even in tropical temperatures, and he''s headed south to settle some old scores.', 'tt0239496', '/uUWunRYYxkPNL6sLiwmyfIiw3sl.jpg');
