insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12770, 'Spiderwebhouse', False, True, False, False, 2015, 6.2, 'When 12-year-old Jonas’ mother leaves to “fight her demons”, he steps up as head of the household, becoming the unofficial guardian to his younger siblings. With food and money running out, the children retreat into a world of their own where, cloaked eerily in black-and-white, their home turns into one of bugs and mysticism – they make saucepan gardens, they take on insects as pets and spiderwebs encroach. Only the friendship of an odd young homeless man gives Jonas hope to survive in an adult’s world.', 'tt3203954', '/wEtw1SylMnZ2YlNUSaiAjCqJ72g.jpg');
