insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12841, 'Cold Blood', False, True, False, False, 1975, 5, 'Corinna witnesses how three guys chase and shoot a man in front of her lonesome house. As only witness, they force her to come with them and care for the guy''s wound. But she manages to flee shortly after and takes Blondi to a doctor. He tells her his story, how he used to smuggle drugs, but one day fled with a suitcase full of money. Corinna inexplicably falls in love with him and decides to accompany him on his further flight. But the villains are close behind them.', 'tt0071138', '/x2YEr1zlAHRFcN3nk0aMOm7QF9G.jpg');
