insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12886, 'The Butcher', False, True, False, False, 2006, 3.6, 'An unlikely friendship between a dour, working class butcher and a repressed schoolteacher coincides with a grisly series of Ripper-type murders in a provincial French town.', 'tt0064106', '/rtvgY5pSIxmLUJkiLIIcXcvcHTi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12888, 'Reaching for the Moon', False, True, False, False, 1930, 5.6, 'In 1951 New York poet Elizabeth Bishop travels to Rio de Janeiro to visit Mary, a college friend. The shy Elizabeth is overwhelmed by Brazilian sensuality. She is the antithesis to Mary’s dashing partner, architect Lota de Macedo Soares. Although frosty at first, the architect soon makes a play for Elizabeth and the poet finally succumbs to Lota’s advances. Mary is jealous, but unconventional Lota is determined to have both women at all costs. Their ménage à trois is thrown off balance when Lota starts work on her biggest project to date, designing Parque do Flamengo in Rio. Elizabeth accepts an academic teaching post in the USA and the women drift apart. Lota, at all other times brimming with self-confidence, is inconsolable. This eternal triangle plays out against the backdrop of the military coup of 1964. Bishop’s moving poems are at the core of a film which lushly illustrates a crucial phase in the life of this influential Pulitzer prize-winning poet', 'tt2217458', '/rzjiarcKqZYjtBI9Tg5Mo4jVT4v.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12892, 'Maybe Baby', False, True, False, False, 1988, 5.5, 'Hugh Laurie (Sam) and Joely Richardson (Lucy) star as an irresistible couple who seem to have everything - good looks, successful careers, matching motorbikes and an enthusiastic love life. The only thing they lack is the one thing that they really want - a baby. Sam sets his sights on writing a successful screenplay, a comedy about a couple trying for a baby. The only problem is that Lucy is horrified at the idea and forbids him to do it. Creative fulfillment is too much for Sam to resist and meanwhile an increasingly hormonal Lucy is distracted by the attentions of the handsome Carl Phipps (James Purefoy). Sam and Lucy''s love for one another, the most important thing they have, will now truly be put to the test ...', 'tt0206926', '/4PLm3BZiOXhDIbbqH8ipVyPVA1M.jpg');
