insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (12903, 'Marathon', False, True, False, False, 2010, 6.5, '20-years-old Cho-Won (Cho Seung-Woo) is young man with autism. He lives with his highly protective mother Kyung-Sook (Kim Mi-Sook) and younger brother (Baek Sung-Hyun), who sometimes resents the attention his older brother receives from his mother. Through special schooling and taking part in physical activities, Cho-Won has adjusted relatively well into society. Cho-Won also seems to have a knack for running. After placing third in a race, his mother decides to hire a proper coach to train her son. Cho-Won then decides to take part in a marathon. Cho-Won hopes to break the marathon''s fastest record of three hours.', 'tt0448621', '/7ORuGI8SehIEoAE3Mf52P2abW2.jpg');
