insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (13029, 'Payday', False, True, False, False, 2013, 7.7, 'A cynical look at the life of a not so nice country-western singer, Maury Dann - played by Rip Torn in one of his most memorable performances. He ruthlessly manipulates every one around him to suit his selfish needs...he even gets his limo driver to take the blame for the death of a fan which he stabbed.', 'tt0069067', '/j8vpXIM7NR8Iu5YfPCa3NIFDp06.jpg');
