insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (13113, 'Brothers O''Toole', False, True, False, False, 1973, 4.5, 'The O''Toole Brothers are Eastern con men, exceptionally good at talking their way out of tight situations. When they ride into Molybdenum, Colorado, not suspecting the riches beneath the streets, they turn the sleepy mining town upside-down for their search for the gold. High-spirited hijinks ensue, with the brothers involved in everything from stolen gambling equipment to a "belchin'', cussin'' and spittin'' " contest.', 'tt0069825', '/6robv5HtqWnfpTv9IZZelTGlsb4.jpg');
