insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14012, '500 MPH Storm', False, True, False, False, 2013, 2.2, 'When an energy experiment goes haywire, a rash of massive hurricanes rips across North America. A high school science teacher must get his family to safety before the hurricanes merge, creating a "hypercane" with the power to wipe the US off the map.', 'tt2518848', '/vdWnIULCa3VgqVVPiL6EXumIPk7.jpg');
