insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14496, 'Last Train Home', False, True, False, False, 1989, 5.7, 'A family embarks on an annual tormenting journey along with 200 other million peasant workers to reunite with their distant family, and to revive their love and dignity as China soars as the world''s next super power.', 'tt1512201', '/nguiji6Kk0SKfBK7ciaZtL0yj6l.jpg');
