insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14513, 'Fatty Drives the Bus', False, True, False, False, 1999, 4.3, 'Oh no! It appears as if Satan isn''t going to fill his soul quota this month! So of course, he turns himself into a tour guide and takes a bus-load of unsuspecting tourists on a ride down the highway to hell.', 'tt0262411', '/bM7q99Lk3mirlNTDR4B0Izohb0l.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14519, 'Age of Tomorrow', False, True, False, False, 2014, 2.1, 'Mankind must fight to survive as Earth is invaded by hostile UFOs bent on destroying the planet. As the epic battle wages on, astronauts sneak aboard the mothership where they discover a portal to the aliens'' home world. They manage to alert Earth before they are brutally murdered. Left with no other choice, Earth sends an elite military team of science and combat specialists through the portal where they attempt to stop the invasion from the inside out.', 'tt3677466', '/rij2GSSXbhNLCFdvJN8YmRRD2i3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14532, 'Childless', False, True, False, False, 2015, 5.7, 'Katherine is a typical teenager. Today''s her funeral. The four adults in her life have a lot on their mind - and it''s not all about Katherine either. With a frankness that''s strikingly disarming as well as frequently self-serving, the grown-ups struggle with being... well... grown-up.', 'tt0867270', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14547, 'Jurassic City', False, True, False, False, 2014, 3.3, 'When a top-secret laboratory is unexpectedly breached, thousands of rampaging raptors are unleashed on Los Angeles! A black-ops unit is mobilized to contain the creatures before they cause city-wide chaos. Simultaneously, a truckload of raptors is rerouted to a nearby prison. Upon their escape, these ferocious flesh-eaters are beyond containment. This is Jurassic judgment night for smoking hot sorority girls, sinister scientists, muscle-bound military and doomed death-row inmates! It''s about to get bloody in Jurassic City!', 'tt2905674', '/vhpcMRNjB1hiKJPS4QPpmV5Mpvn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14558, 'Dead Men Walk', False, True, False, False, 1943, 4.7, 'We meet Doctor Lloyd Clayton at the funeral of his twin brother, evil magician Elwyn. Zolarr, Elwyn''s hunchbacked servant, acccuses Lloyd of Elwyn''s murder, but Lloyd claims it was self-defense. Lloyd''s niece Gayle and her fiance Harper soon find that Elwyn''s evil influence is still at work.', 'tt0035784', '/1AqEIyA6BndGYcNkcUJwXxvEFjq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14578, 'Top of the World', False, True, False, False, 1998, 4.7, 'In Las Vegas for a quicky divorce, a just-paroled ex-cop and his wife wander into the Top of the World Casino, run by the shady Charles Atlas. They win big, right as the casino is being robbed. The police believe their big win was a staged diversion, and the two of them become suspects. Over the course of the evening and next morning, the two attempt to escape to surrounded casino, and to prove their innocence, as well as to save their marriage.', 'tt0120353', '/mPLVG4xCDrhDzMETqp6DENPvUkD.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14586, 'Snakehead Terror', False, True, False, False, 2004, 3.8, 'A small town, desperate to recover from hard economic times, is under threat when voracious Snakehead fish mutate and survive previous lake chemical poisonings. The fish transform from pests to predators when human growth hormones are dumped into the local lake in the hopes of reviving the local fishing industry. Thriving on the hormones, the Snakehead fish grow to monstrous proportions, devouring everything within reach. Capable of moving and eating on land, they are forced to leave the now barren lake in a desperate search for food - animal, vegetable or human.', 'tt0371954', '/pyiyftwx05BIsx2pephTuadHEnX.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14587, 'Way of the Wicked', False, True, False, False, 2014, 3.8, 'Lawrence Salva''s script centers on a detective (Facinelli), on the trail of a murderer, who is led to believe that a local teenager, harboring some sort of strange supernatural power, may be involved. Slater plays the man''s disbelieving boss, while Hauer plays a man-of-the-cloth who aids the cop on his mission.', 'tt2483208', '/sNTui6AC37DVMMcvL4QVmOWroSI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14589, 'Super Hero Party Clown', False, True, False, False, 2012, 6.7, 'Eugene Stimpson is a superhero... At least he plays one at birthday parties. When his high school nemesis Todd gets a job playing a rival superhero, Eugene must struggle to keep his identity secret while competing with Todd for the affections of the same girl. Can he keep his identity concealed long enough to make his move and defeat Todd, or will the mounting lies be too much for him to handle?', 'tt1663675', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14590, 'Abraxas, Guardian of the Universe', False, True, False, False, 1990, 2.8, 'An alien policeman comes to Earth to hunt down a renegade of his own race.', 'tt0101264', '/1DwaSuBcK2bdWFhVa543DYYmDE2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14596, 'Zero Tolerance', False, True, False, False, 2014, 5.6, 'Johan is a two-fisted Gothenburg cop who finds himself in a shoot-out with jewel robbers. After the smoke has cleared, one robber, shot by his accomplice, and an innocent bystander, are dead. Three witnesses, including Helen, identify thug extraordinaire Leo Gaut as being the dead crook''s trigger-happy colleague. Gaut soon threatens the three witnesses, and only Johan, the badge-wearing hero, can save them.', 'tt0198837', '/knBAD3XdpSwjOvZZ6JGCZoQjADL.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14612, 'Spiders 2: Breeding Ground', False, True, False, False, 2001, 3, 'Jason and Alexandra ''Alex'', a close couple, loose their sail yacht in a storm and are grateful to get picked up by Captain Jim Bigelow''s commercial carrier. Suspicous about the rough treatment and injections given by ship''s doctor Gerbac which make the strong man faint, and realizing the ship is improbably empty and the radio not broke as the crew claims, Jason starts snooping around and finds human bodies on meat hooks, but even Alex thinks he''s just hallucinating. Alas while sane Jason is restrained for his ''paranoia'', the real madmen are the doc''s genetic experiment outfit, who abuse their human captives as live incubators for a giant spider''s eggs...', 'tt0282179', '/3HG6vktKdJGy06PRLLwuw8vUdEc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (14620, 'The Monkey Hustle', False, True, False, False, 1976, 5.2, 'A new highway threatens a Chicago neighborhood, so to protest the residents throw a block party.', 'tt0076404', '/ltadfkSjaEptRQUZjC3EzthbOcA.jpg');
