insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15005, 'Stalker', False, True, False, False, 2010, 4.4, 'When novelist Paula Martin retreats to the seclusion of her family home Crows Hall she hopes to clear her mind and focus on her new book. The arrival of an assistant, Linda, should take the pressure off... but as the bodies pile up, Paula finds herself trapped in a terrifying nightmare of murder and madness.', 'tt1411236', '/f6W5ngplpuBGYwC6FMuWwskR4kE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15011, 'Dead Shadows', False, True, False, False, 2012, 3.7, 'Dead Shadows tells the terrifying story about a young man, Chris, whose parents were brutally killed 11 years ago, on the same day that the comet of Haley could be seen from earth. Tonight, a new comet is going to appear and everyone in Chris’s building are getting ready for a big apocalypse party to celebrate the event. As the night falls, Chris starts to discover that people are acting strange and it seems to somehow be connected to the comet. People are becoming disoriented and violent and it doesn’t take long before they start mutating into something from beyond this world.', 'tt1927012', '/iLy35VlOa3h5UGSTUR9umGImby2.jpg');
