insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15136, 'Pro Wrestlers vs Zombies', False, True, False, False, 2014, 3.5, 'The Franchise Shane Douglas accidentally kills a wrestler in the ring. The wrestler''s brother Angus seeks revenge by surrendering his soul to an ancient demon to gain the power to raise the undead. Angus then pays for a private show at an empty prison to lure Douglas and his friend Rowdy Roddy Piper into a death trap.', 'tt2240046', '/AvrKGPls7MmTDkozfuNC33TfXwY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15138, 'Bending Colours', False, True, False, False, 2012, 5.6, 'Bending Colours is a moving portrait of Jordy Smith. This is not a biopic. It''s a never-before seen insight into game changing next level surfing. From an average school kid in South Africa, a cocky tour rookie, to the professional world title threat he is today, Bending Colours tells a story. No one does progressive surfing or the hi-fi quite like Kai Neville. The fit is obvious. You know Modern Collective. You know the new breed of surfer, soundtrack and star it''s created. This next collaboration will further redefine and change the game.', 'tt2723912', '/3Mn7t14EIiM9s7GmSe9SaUm8umI.jpg');
