insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15151, 'Age of Ice', False, True, False, False, 2014, 1.8, 'Massive earthquakes open the Arabian tectonic plate, resulting in unstable weather and freezing temperatures that will be unsurvivable by nightfall. Attempting to reach safety, a vacationing family in Egypt must battle the rapidly cooling temperatures that usher in a new Ice Age, covering the Sphinx, Pyramids, and Sahara Desert with mountains of snow.', 'tt3924510', '/sevOgSERN3kmjSf1tEpoQ9B43vm.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15156, 'Octopus 2: River of Fear', False, True, False, False, 2001, 2.9, 'Dead bodies are being found in New York harbor. The police have no clues nor suspects until Nick and his colleague realize the killer is a giant octopus. Everybody, especially the police captain, refuses to believe Nick''s story, and soon the harbor will be filled with boats for the 4th of July celebrations.', 'tt0273907', '/b5umCfrvm2TlHrYyWU5Yu57t1lK.jpg');
