insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15313, 'The Invoking', False, True, False, False, 2013, 2.7, 'After inheriting a house from the family she never knew, Samantha Harris (Trin Miller) and three friends head to rural Sader Ridge to inspect the property. Soon after arriving, Sam begins to experience horrific visions of savage brutality and unspeakable evil. Plagued by the sinister forces closing in around her, Sam descends into a waking nightmare when the demons from her past refuse to stay buried any longer.', 'tt2435514', '/vix4kqsAlEfLVXt31NKUMqjK0fN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15314, 'The Brussels Business', False, True, False, False, 2012, 6.8, 'The Brussels Business is a docu-thriller that dives into the grey zone underneath European democracy. An expedition into the world of the 15,000 lobbyists in the EU-capital, of the PR-conglomerates, think tanks and their all embracing networks of power and their close ties to the political elites.', 'tt2279313', '/8exJHRJaLGxp8inmCugHXgravcz.jpg');
