insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15324, 'American Justice', False, True, False, False, 2015, 2.8, 'When the reckless actions of an out-of-control LAPD officer get his partner killed in a deadly hostage situation, he gets suspended from the police force and heads to Mexico to face off against corrupt cops.', 'tt2940482', '/1IM5IjXGqGMvtL170viGpwyA7ix.jpg');
