insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15390, 'Angel of Death', False, True, False, False, 2002, 4.4, 'In the tradition of Grindhouse, Kill Bill and Sin City, award-winning comic book writer Ed Brubaker (Incognito, The Death of Captain America, Daredevil) teams up with stuntwoman-turned-cult star Zoe Bell (Death Proof, TV''s "Lost") to deliver a stark, stylish pulp thriller about a very bad girl gone "good."', 'tt1269734', '/xbNgVKUVOhe4x3ZChhX8hHTzvgY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15391, 'Swamp Zombies!!!', False, True, False, False, 2005, 2.6, 'A chief physician at a large metropolitan hospital is formulating a serum to resurrect recently deceased "patients".When his facility comes under inspection from the federal government, the doctor is forced to dispose of the "patients" as quick as possible even though they are in mid-experiment and he doesn''t know if his serum even will work. With the help of the evil hospital operations manager, he manages to ditch the experiments in the neighboring swamplands. Little do they know, their serum works, the experiments rise to "life" and wreck havoc on a group of students conducting science experiments at the swamplands. With the help of a swamp hermit, a beautiful innocent bystander, a park ranger, and the police captain they try to piece together what has gone on... as well as survive', 'tt0439309', '/35C8f8pbxZyhRPiEHVhaOkfSuPb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15399, 'Crash of Moons', False, True, False, False, 1954, 3.1, 'A three-part episode from the TV series Rocky Jones, Space Ranger edited together and released as a feature for 16mm rental only. Season 1 episodes 21, 22, 23.  Episode 21: Rocky saves a space station and his friends when they are trapped between gypsy moons. Episode 22: Cleolanthe tries to destroy one of the gypsy moons with a barrage of missiles. Episode 23: Rocky evacuates the gypsy moon Posetta and stops Cleolanthe''s missile barrage.', 'tt0045655', '/9ItQQY9H03fNDwWHlYWcCxvH5BC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15401, 'Blubberella', False, True, False, False, 2011, 2.3, 'An action comedy centered on Blubberella, an overweight half-vampire woman whose footsteps cause explosions and whose dual swords are used against anyone who makes fun of her. She must face an army of undead Nazi soldiers in her valiant struggle against bloodshed and tyranny.', 'tt1756427', '/xKEPIK6dGMxLk7ryfwrJVJfw4Pj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15407, 'On Probation', False, True, False, False, 1935, 4.4, 'Díaz is a burly seen-it-all cop who''s depressed about his marriage. Silverstein is a skinny Jewish shrink who''s assigned to help him back to his job. Soon Silverstein is taking advice from Díaz about *his* marriage, and is learning how to handle a gun. Good thing, for they find themselves in a stew of murder, arms dealers and corrupt cops, and only their new friendship will get them out alive.', 'tt0462570', '/vdfbyrsfzE4AlkqAmLX0nIZldL8.jpg');
