insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15441, 'Revolt of the Zombies', False, True, False, False, 1936, 3.3, 'The story is set in Cambodia in the years following WWI. Evil Count Mazovia (Roy D''Arcy) has come into possession of the secret methods by which dead men can be transformed into walking zombies and uses these unholy powers to create a race of slave laborers. An expedition is sent to the ruins of Angkor Wat, in hopes of ending Mazovia''s activities once and for all. Unfortunately, Armand (Dean Jagger), one of the members of the expedition, has his own agenda.', 'tt0028174', '/sxdDzpgriKSEGAkP7igl0otJcwJ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15445, 'Two-Bit Waltz', False, True, False, False, 2014, 5, 'Suspension from school, the loss of a friend, a broken heart and lack of inspiration lead to Maude''s downfall in this romp through teenage error.  Your teenage years are never easy… but for Maude, things couldn''t be worse. Within one week, she is suspended from school, stranded by her best friend, dumped by the boy she loved and inherits an enormous amount of money with the passing of her grandmother– only to be claimed under one condition: Maude must prove by age 18 that she knows exactly what to do with her life. But with her 18th birthday rapidly approaching, Maude must dive into a world of self-discovery or else lose the inheritance.', 'tt2570224', '/l2icc2wJQTbUadBHBPBmehTRANM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15457, 'InAlienable', False, True, False, False, 2008, 3.4, 'Still guilt-ridden over the accident that took his family''s lives, Eric Norris discovers that his body is host to a parasite from another world. Except, it is more than a parasite: it carries his DNA.', 'tt1027874', '/Lx4xVtPFlujTWdyrweYvp0r4Yg.jpg');
