insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15589, 'Operation Kid Brother', False, True, False, False, 1967, 3.5, 'The evil crime syndicate Thanatos is bent on taking over the world, using a magnetic wave generator that will cause all metal-based machinery to grind to a halt. However, the well-known British secret agent normally assigned to such tasks isn''t available, so they engage his civilian brother, Neil, to help. Neil, played by Neil Connery, is a world-class plastic surgeon, hypnotist, and lip-reader, which turn out to be precisely the skills required for thwarting Thanatos.', 'tt0062078', '/vZbOOaFtoXPTAclJEooB2y3r6u4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15590, 'Creep Van', False, True, False, False, 2012, 3.9, 'When Campbell, a vehicle-less 20-something slacker, innocently makes the mistake of answering a FOR SALE sign placed on a dilapidated van, suspense and action take over as he is suddenly caught-up in a deadly game of cat and mouse that puts everyone close to him in mortal danger.', 'tt1844643', '/5FbY7rUzLhSJ0psLOqgE5zyTJ7C.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15592, 'The Da Vinci Treasure', False, True, False, False, 2006, 2.2, 'An anthropologist must unlock the code hidden in the works of Leonardo Da Vinci in order to find the greatest treasure ever, one that could change Christianity forever.', 'tt0810817', '/6JuiYppSTU35JgnHiKujSEGsg7y.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15593, 'Elf Bowling the Movie', False, True, False, False, 2007, 1.8, 'So you think you know the real story behind Santa Claus? So starts "Elf Bowling the Movie: The Great North Pole Elf Strike" and it takes you on a laugh filled journey from pirate filled oceans to the North Pole to Fiji. There are several toe tapping musical numbers along the way including Wrapplestiltskin rapping and Dingle, the evil brother, crooning. Oh and let''s not forget the "Elf Bowling". I''m not familiar with the video game but it sure is funny seeing how the game is fit into the film. The elves signature reaction of joy cracked me up. First rate writing, brilliant voice work by Joe Alaskey and Tom Kenney (of Spongebob fame), and superb animation will make this a holiday treat that everyone will enjoy.', 'tt0460811', '/oFcHKHaI7vvqD5jYLhTPhUR3eCB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15605, 'Round Trip to Heaven', False, True, False, False, 1992, 3.8, 'Since Larry works at a garage, he gets to use one of the Rolls Royces. There is only one problem, there is a briefcase full of money in the trunk. So when Larry and his cousin Steve decide to go to Palm Springs to look for Ms. Right at a popular beauty pageant, the owner of the briefcase will do the impossible to get it back.', 'tt0105289', '/hhFI3nbCzFAX872wZV9HsopsTsE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15610, 'The Magic of Heineken', False, True, False, False, 2014, 6.3, 'Take a captivating ride through nearly 150 years of entertaining history telling the story of the world''s largest family controlled brewer. Meet Gerard Adriaan Heineken, who founded the first Heineken Brewery in 1864 and follow this extraordinary tale of pioneering adventure, relentless obsession for quality, persistent innovation, environmental sustainability, and celebrated sports and music events.', 'tt3384890', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15611, 'Trojan Eddie', False, True, False, False, 1996, 6.3, 'The story of Eddie, a small town ex con, who discovers he has talent for selling anything and everything. Eddie sees a way to rise above the low life by setting up on his own; what he didn''t bargin for was the murder and mahem along the way.', 'tt0117969', '/qY76v1nfbeb6jQ0unP24ZtkDbMD.jpg');
