insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15696, 'Boots and Saddles', False, True, False, False, 1937, 5.8, 'Young Englishman inherits ranch which he wants to sell, but Gene''s gonna turn him into a real westerner instead. When new owner Spud arrives from England, Autry convinces him not to sell the ranch but to raise horses for the Army. When both Autry''s and Neale''s bids are the same, the Colonel calls for a race to decide the winner. But that night Neale has Autry''s stable burned.', 'tt0028646', '/4O7EpL6LUm2FPktdcREXBY0jWyE.jpg');
