insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15708, 'Back from Hell', False, True, False, False, 2012, 2.9, 'When six pals escape the chaos of the city for a brief getaway, the manor home they rent in the countryside may not be so idyllic after all… The party mood soon sours as strange phenomenon begins. The group initially attempts to rationalize the occurrences, but they soon escalate into horrifying paranormal events and, ultimately, the apparent possession of Giorgio.  Desperate, the terrorized group pleads with the local priest to exorcise the unholy force. But its demonic powers may be more than he can handle… and a bigger menace than any of them could have imagined.', 'tt2078613', '/7qP8MzapfdojnV3o02QOTVTte2Z.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15713, 'The Violent Years', False, True, False, False, 1956, 3.2, 'A newspaper publisher''s daughter suffers from neglect by her parents. She and her friends turn to crime by dressing up like men, holding up gas stations, raping young men at gunpoint, and having makeout parties when her parents are away. Their "fence" gets them to trash the school on request of sinister un-American clients, and they run afoul of the law, apple pie, and God himself.', 'tt0049922', '/z6ECWnLIfeOLgMGHDCT3Dft85wy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (15720, 'Toxin', False, True, False, False, 2015, 2.7, 'Lieutenant John Paxton wants revenge against the government he and his men swore their lives to defend. The very government that abandoned and betrayed him, leaving him to die after a secret biological warfare experiment went horribly wrong. John will discover revenge is a journey that will ultimately leave millions infected, countless dead, and the lucky few survivors struggling to find the last safe corner for humanity.', 'tt2118009', '/b0pU2t0Zb6L4j2wO8EWjrN8Pve.jpg');
