insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16247, 'Tangled', False, False, True, False, 2010, 7.7, 'A young man is found bruised, beaten and stumbling down a secluded road. As the police try to piece together what happened, the convoluted relationship between a young woman and her two suitors gradually emerges.', 'tt0238137', '/eAfVPfmWbGDhFpRfcLhIj4434SK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16248, 'Wreck-It Ralph', False, False, True, False, 2012, 7.7, 'Wreck-It Ralph is the 9-foot-tall, 643-pound villain of an arcade video game named Fix-It Felix Jr., in which the game''s titular hero fixes buildings that Ralph destroys. Wanting to prove he can be a good guy and not just a villain, Ralph escapes his game and lands in Hero''s Duty, a first-person shooter where he helps the game''s hero battle against alien invaders. He later enters Sugar Rush, a kart racing game set on tracks made of candies, cookies and other sweets. There, Ralph meets Vanellope von Schweetz who has learned that her game is faced with a dire threat that could affect the entire arcade, and one that Ralph may have inadvertently started.', 'tt1772341', '/nsUAgWCxqbTD9wkKrv3nBGH2DVk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16249, 'Ant-Man', False, False, True, False, 2015, 7.3, 'Armed with the astonishing ability to shrink in scale but increase in strength, master thief Scott Lang must embrace his inner-hero and help his mentor, Doctor Hank Pym, protect the secret behind his spectacular Ant-Man suit from a new generation of towering threats. Against seemingly insurmountable obstacles, Pym and Lang must plan and pull off a heist that will save the world.', 'tt0478970', '/rQRnQfUl3kfp78nCWq8Ks04vnq1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16251, 'Avengers: Age of Ultron', False, False, True, False, 2015, 7.3, 'When Tony Stark tries to jumpstart a dormant peacekeeping program, things go awry and Earth’s Mightiest Heroes are put to the ultimate test as the fate of the planet hangs in the balance. As the villainous Ultron emerges, it is up to The Avengers to stop him from enacting his terrible plans, and soon uneasy alliances and unexpected action pave the way for an epic and unique global adventure.', 'tt2395427', '/4ssDuvEDkSArWEdyBl2X5EHvYKU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16252, 'Mary Poppins', False, False, True, False, 1964, 7.8, 'The movie combines a diverting story, songs, color and sequences of live action blended with the movements of animated figures. Mary Poppins is a kind of Super-nanny who flies in with her umbrella in response to the request of the Banks children and proceeds to put things right with the aid of her rather extraordinary magical powers before flying off again.', 'tt0058331', '/ei8hhYCMfURfPOXKBnyl61be2iV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16253, 'Finding Dory', False, False, True, False, 2016, 7.3, 'Dory is reunited with her friends Nemo and Marlin in the search for answers about her past. What can she remember? Who are her parents? And where did she learn to speak Whale?', 'tt2277860', '/3UVe8NL1E2ZdUZ9EDlKGJY5UzE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16254, 'Who Framed Roger Rabbit', False, False, True, False, 1988, 7.7, '''Toon star Roger is worried that his wife Jessica is playing pattycake with someone else, so the studio hires detective Eddie Valiant to snoop on her. But the stakes are quickly raised when Marvin Acme is found dead and Roger is the prime suspect.', 'tt0096438', '/lYfRc57Kx9VgLZ48iulu0HKnM15.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16255, 'Mulan', False, False, True, False, 1998, 7.6, 'A tomboyish girl disguises herself as a young man so she can fight with the Imperial Chinese Army against the invading Huns. With help from wise-cracking dragon Mushu, Mulan just might save her country -- and win the heart of handsome Captain Li Shang.', 'tt0120762', '/bJ0DxWQDLdjSJmFHwtximVdgJ5P.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16256, 'Iron Man 3', False, False, True, False, 2013, 7.2, 'When Tony Stark''s world is torn apart by a formidable terrorist called the Mandarin, he starts an odyssey of rebuilding and retribution.', 'tt1300854', '/qhPtAc1TKbMPqNvcdXSOn9Bn7hZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16257, 'The Sound of Music', False, False, True, False, 1965, 8, 'Film adaptation of a classic Rodgers and Hammerstein musical based on a nun who becomes a governess for an Austrian family.', 'tt0059742', '/nv8bcyqXJh2tijWLa3MoUktlVZZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16258, 'Ice Age', False, False, True, False, 2002, 7.5, 'With the impending ice age almost upon them, a mismatched trio of prehistoric critters – Manny the woolly mammoth, Diego the saber-toothed tiger and Sid the giant sloth – find an orphaned infant and decide to return it to its human parents. Along the way, the unlikely allies become friends but, when enemies attack, their quest takes on far nobler aims.', 'tt0268380', '/gLhHHZUzeseRXShoDyC4VqLgsNv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16259, 'Cars', False, False, True, False, 2006, 7.1, 'Lightning McQueen, a hotshot rookie race car driven to succeed, discovers that life is about the journey, not the finish line, when he finds himself unexpectedly detoured in the sleepy Route 66 town of Radiator Springs. On route across the country to the big Piston Cup Championship in California to compete against two seasoned pros, McQueen gets to know the town''s offbeat characters.', 'tt0317219', '/qa6HCwP4Z15l3hpsASz3auugEW6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16260, 'Snow White and the Seven Dwarfs', False, False, True, False, 1937, 7.6, 'A beautiful girl, Snow White, takes refuge in the forest in the house of seven dwarfs to hide from her stepmother, the wicked Queen. The Queen is jealous because she wants to be known as "the fairest in the land," and Snow White''s beauty surpasses her own.', 'tt0029583', '/b43jrEguirXjtAVgXMiTvs8wUhC.jpg');
