insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16264, 'A Bug''s Life', False, False, True, False, 1998, 7.2, 'On behalf of "oppressed bugs everywhere," an inventive ant named Flik hires a troupe of warrior bugs to defend his bustling colony from a horde of freeloading grasshoppers led by the evil-minded Hopper.', 'tt0120623', '/kHgWI1AbYnYPZrUl5DCwzq9tyB2.jpg');
