insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16270, 'The Emperor''s New Groove', False, False, True, False, 2000, 7.3, 'Kuzco is a self-centered emperor who summons Pacha from a village and to tell him that his home will be destroyed to make room for Kuzco''s new summer home. Kuzco''s advisor, Yzma, tries to poison Kuzco and accidentally turns him into a llama, who accidentally ends up in Pacha''s village. Pacha offers to help Kuzco if he doesn''t destroy his house, and so they form an unlikely partnership.', 'tt0120917', '/wwbgkXQBEKtnyIJapk6gUgWkVw8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16271, 'The Straight Story', False, False, True, False, 1999, 8, '"The Straight Story" chronicles a trip made by 73-year-old Alvin Straight from Laurens, Iowa, to Mt. Zion, Wisconsin, in 1994 while riding a lawn mower. The man undertook his strange journey to mend his relationship with his ill, estranged, 75-year-old brother Lyle.', 'tt0166896', '/tT9cMiVDdtlcdZxOoFy3VRmEoKk.jpg');
