insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16274, 'Pinocchio', False, False, True, False, 1940, 7.4, 'Geppetto the carpenter carves the object of his hidden desires out of a log: a puppet that will keep him company and will be like a son to him. He will call him Pinocchio. Imagine his surprise when he discovers that the puppet moves, as if by magic, and is gifted with a life on its own.', 'tt1693039', '/z4I6ngsboK7RiuN4PGDxEI8EO2U.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16275, 'Thor: The Dark World', False, False, True, False, 2013, 6.9, 'Thor fights to restore order across the cosmos… but an ancient race led by the vengeful Malekith returns to plunge the universe back into darkness. Faced with an enemy that even Odin and Asgard cannot withstand, Thor must embark on his most perilous and personal journey yet, one that will reunite him with Jane Foster and force him to sacrifice everything to save us all.', 'tt1981115', '/wp6OxE4poJ4G7c0U2ZIXasTSMR7.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16277, 'Lilo & Stitch', False, False, True, False, 2002, 7.2, 'A lonely Hawaiian girl named Lilo is being raised by her older sister, Nani, after their parents die -- under the watch of social worker Cobra Bubbles. When Lilo adopts a funny-looking dog and names him "Stitch," she doesn''t realize her new best friend is a wacky alien created by mad scientist Dr. Jumba.', 'tt0275847', '/d73UqZWyw3MUMpeaFcENgLZ2kWS.jpg');
