insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16284, 'The Chronicles of Narnia: The Lion, the Witch and the Wardrobe', False, False, True, False, 2005, 6.9, 'Siblings Lucy, Edmund, Susan and Peter step through a magical wardrobe and find the land of Narnia. There, the they discover a charming, once peaceful kingdom that has been plunged into eternal winter by the evil White Witch, Jadis. Aided by the wise and magnificent lion, Aslan, the children lead Narnia into a spectacular, climactic battle to be free of the Witch''s glacial powers forever.', 'tt0363771', '/kzJip9vndXYSKQHCgekrgqbnUrA.jpg');
