insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16291, 'Star Wars: Episode I - The Phantom Menace', False, False, True, False, 1999, 6.5, 'Anakin Skywalker, a young slave strong with the Force, is discovered on Tatooine. Meanwhile, the evil Sith have returned, enacting their plot for revenge against the Jedi.', 'tt0120915', '/6wkfovpn7Eq8dYNKaG5PY3q2oq6.jpg');
