insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16297, 'Holes', False, False, True, False, 2003, 7, 'Stanley''s family is cursed with bad luck. Unfairly sentenced to months of detention at Camp Green Lake, he and his campmates are forced by the warden to dig holes in order to build character. What they don''t know is that they are digging holes in order to search for a lost treasure hidden somewhere in the camp.', 'tt0311289', '/w3KHhv3Lb42lqdd95mAxYwM4AcF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16298, 'Pete''s Dragon', False, False, True, False, 2016, 6.7, 'Pete is a mysterious 10-year-old with no family and no home who claims to live in the woods with a giant, green dragon named Elliott. With the help of Natalie, an 11-year-old girl whose father Jack owns the local lumber mill, forest ranger Grace sets out to determine where Pete came from, where he belongs, and the truth about this dragon.', 'tt2788732', '/A9x4Ogk8KUXTfU649NS6lS9TCmu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16299, 'The Good Dinosaur', False, False, True, False, 2015, 6.7, 'An epic journey into the world of dinosaurs where an Apatosaurus named Arlo makes an unlikely human friend.', 'tt1979388', '/dM50iVbcnzQUIoc9TmUUk0SP4P4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16300, 'Frankenweenie', False, False, True, False, 2012, 6.9, 'When young Victor''s pet dog Sparky (who stars in Victor''s home-made monster movies) is hit by a car, Victor decides to bring him back to life the only way he knows how. But when the bolt-necked "monster" wreaks havoc and terror in the hearts of Victor''s neighbors, he has to convince them (and his parents) that despite his appearance, Sparky''s still the good loyal friend he''s always been.', 'tt0087291', '/yVaibhNTphPgxdEWfuvtELgxePt.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16301, 'Tron', False, False, True, False, 1982, 6.8, 'As Kevin Flynn searches for proof that he invented a hit video game, he is "digitalized" by a laser and finds himself inside The Grid, where programs suffer under the tyrannical rule of the Master Control Program. With the help of TRON, a security program, Flynn seeks to free The Grid from the MCP.', 'tt0084827', '/zwSFEczP7AzqugAHHIX3zHniT0t.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16302, 'Winnie the Pooh', False, False, True, False, 2011, 7.2, 'During an ordinary day in Hundred Acre Wood, Winnie the Pooh sets out to find some honey. Misinterpreting a note from Christopher Robin, Pooh convinces Tigger, Rabbit, Piglet, Owl, Kanga, Roo, and Eeyore that their young friend has been captured by a creature named "Backson" and they set out to save him.', 'tt1449283', '/wMZU69sPEP8dSNU0nMWVt7b54EQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16303, 'TRON: Legacy', False, False, True, False, 2010, 6.8, 'Sam Flynn, the tech-savvy and daring son of Kevin Flynn, investigates his father''s disappearance and is pulled into The Grid. With the help of a mysterious program named Quorra, Sam quests to stop evil dictator Clu from crossing into the real world.', 'tt1104001', '/vuifSABRpSnxCAOxEnWpNbZSXpp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16304, 'The Sword in the Stone', False, False, True, False, 1963, 7.2, 'Wart is a young boy who aspires to be a knight''s squire. On a hunting trip he falls in on Merlin, a powerful but amnesiac wizard who has plans for him beyond mere squiredom. He starts by trying to give him an education, believing that once one has an education, one can go anywhere. Needless to say, it doesn''t quite work out that way.', 'tt0057546', '/7lyeeuhGAJSNXYEW34S8mJ1bwI8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16305, 'The Aristocats', False, False, True, False, 1970, 7.1, 'When Madame Adelaide Bonfamille leaves her fortune to Duchess and her children -- Bonfamille''s prize family of domesticated house cats -- the butler plots to steal the money and kidnaps the heirs, leaving them out on a country road. All seems lost until the wily Thomas O''Malley Cat and his jazz-playing alley cats come to the Aristocats''s rescue.', 'tt0065421', '/1BVOSmQUhphMgnTxnXyfQ9tL1Sc.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16306, 'Cars 3', False, False, True, False, 2017, 6.7, 'Blindsided by a new generation of blazing-fast racers, the legendary Lightning McQueen is suddenly pushed out of the sport he loves. To get back in the game, he will need the help of an eager young race technician with her own plan to win, inspiration from the late Fabulous Hudson Hornet, and a few unexpected turns. Proving that #95 isn''t through yet will test the heart of a champion on Piston Cup Racing’s biggest stage!', 'tt3606752', '/fyy1nDC8wm553FCiBDojkJmKLCs.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16307, 'Queen of Katwe', False, False, True, False, 2016, 7.4, 'A young girl overcomes her disadvantaged upbringing in the slums of Uganda to become a Chess master.', 'tt4341582', '/tJhLQMcEzwDmxqQxr1n81a3sfd8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16308, 'Homeward Bound: The Incredible Journey', False, False, True, False, 1993, 6.9, 'Remake of the popular Disney classic, this time featuring some well known voices as two dogs and a cat trek across America encountering all sorts of adventures in the quest to be reunited with their owners.', 'tt0107131', '/el6dJEpK97OJRQiQhuiSGk2jkV5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16310, 'Before the Flood', False, False, True, False, 2016, 8.3, 'A look at how climate change affects our environment and what society can do prevent the demise of endangered species, ecosystems, and native communities across the planet.', 'tt5929776', '/2fFTLaLbm8Ak7JBSYQ8Mb3jcFx9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16311, 'The Chronicles of Narnia: Prince Caspian', False, False, True, False, 2008, 6.5, 'One year after their incredible adventures in the Lion, the Witch and the Wardrobe, Peter, Edmund, Lucy and Susan Pevensie return to Narnia to aid a young prince whose life has been threatened by the evil King Miraz. Now, with the help of a colorful cast of new characters, including Trufflehunter the badger and Nikabrik the dwarf, the Pevensie clan embarks on an incredible quest to ensure that Narnia is returned to its rightful heir.', 'tt0499448', '/g2fnrbnvynLIMN8954yL8y6ZXsO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16312, 'The Great Mouse Detective', False, False, True, False, 1986, 7.2, 'When the diabolical Professor Ratigan kidnaps London''s master toymaker, the brilliant master of disguise Basil of Baker Street and his trusted sidekick Dawson try to elude the ultimate trap and foil the perfect crime.', 'tt0091149', '/9uDr7vfjCFr39KGCcqrk44Cg7fQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16313, 'Flight of the Navigator', False, False, True, False, 1986, 6.9, 'This 1986 Disney adventure tells the story of 12-year-old David who lives with his family in Fort Lauderdale, Fla. When he awakens from being accidentally knocked out in the forest near his home, he finds that eight years have passed. His family is overjoyed to have him back, but just as perplexed as he is by the fact that he hasn''t aged. When a NASA scientist discovers a UFO nearby, David gets the chance to unravel the mystery and recover the life he lost.', 'tt0091059', '/dS4jmRcEAmeBjEf5cZAuDtTLOPo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16314, 'James and the Giant Peach', False, False, True, False, 1996, 6.7, 'When the young orphan boy James spills a magic bag of crocodile tongues, he finds himself in possession of a giant peach that flies him away to strange lands.', 'tt0116683', '/hCcndEHrNcfkDGcGgqcVGBgcBCA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16315, 'Willow', False, False, True, False, 1988, 7.3, 'Fearful of a prophecy stating that a girl child will be born to bring about her downfall, the evil Queen Bavmorda imprisons all pregnant women within the formidable stronghold of Nockmaar. A child, Elora Danan, is born in the Nockmaar dungeons and identified as the prophesied child by a birthmark on her arm. However, before the black sorceress arrives to claim the child, Elora''s mother convinces her reluctant midwife to escape with the baby. Willow, a timid farmer and aspiring sorcerer, is entrusted with delivering the royal infant from evil.', 'tt0096446', '/s0HWM0TZ1eKO9prRgz5gF0YGGps.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16316, 'Meet the Robinsons', False, False, True, False, 2007, 6.8, 'In this animated adventure, brilliant preteen inventor Lewis creates a memory scanner to retrieve his earliest recollections and find out why his mother gave him up for adoption. But when the villainous Bowler Hat Guy steals the machine, Lewis is ready to give up on his quest until the mysterious Wilbur Robinson shows up on the scene, whisking Lewis to the future to find the scanner and his mom.', 'tt0396555', '/swsjj0jZtsx53Yp9zBORPwsmGWj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16317, 'Star Wars: Episode III - Revenge of the Sith', False, False, True, False, 2005, 7.5, 'Years after the onset of the Clone Wars, the noble Jedi Knights lead a massive clone army into a galaxy-wide battle against the Separatists. When the sinister Sith unveil a thousand-year-old plot to rule the galaxy, the Republic crumbles and from its ashes rises the evil Galactic Empire. Jedi hero Anakin Skywalker is seduced by the dark side of the Force to become the Emperor''s new apprentice – Darth Vader. The Jedi are decimated, as Obi-Wan Kenobi and Jedi Master Yoda are forced into hiding. The only hope for the galaxy are Anakin''s own offspring – the twin children born in secrecy who will grow up to become heroes.', 'tt0121766', '/xfSAoBEm9MNBjmlNcDYLvLSMlnq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16318, 'Fantasia 2000', False, False, True, False, 1999, 7.2, 'Blending lively music and brilliant animation, this sequel to the original ''Fantasia'' restores ''The Sorcerer''s Apprentice'' and adds seven new shorts.', 'tt0120910', '/6JszBEg8OEbkMzpY5IPTMVzxmYZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16319, 'Tinker Bell', False, False, True, False, 2008, 6.8, 'Journey into the secret world of Pixie Hollow and hear Tinker Bell speak for the very first time as the astonishing story of Disney''s most famous fairy is finally revealed in the all-new motion picture "Tinker Bell."', 'tt0823671', '/3Ma0r1n8kfH7UaQMS7bJ9KsYUjT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16320, 'The Rescuers', False, False, True, False, 1977, 6.9, 'What can two little mice possibly do to save an orphan girl who''s fallen into evil hands? With a little cooperation and faith in oneself, anything is possible! As members of the mouse-run International Rescue Aid Society, Bernard and Miss Bianca respond to orphan Penny''s call for help. The two mice search for clues with the help of an old cat named Rufus.', 'tt0076618', '/gCJuDViPCEuHVmoZUvhOj1jyqfV.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16321, 'Eight Below', False, False, True, False, 2006, 7.3, 'In the Antarctic, after an expedition with Dr. Davis McClaren, the sled dog trainer Jerry Shepherd has to leave the polar base with his colleagues due to the proximity of a heavy snow storm. He ties his dogs to be rescued after, but the mission is called-off and the dogs are left alone at their own fortune. For six months, Jerry tries to find a sponsor for a rescue mission.', 'tt0397313', '/wdbjp6X12WRlLJrMlKrbSgjdpuC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16322, 'Invincible', False, False, True, False, 2006, 7.1, 'Inspired by the true story of Vince Papale, a man with nothing to lose who ignored the staggering odds and made his dream come true. When the coach of Papale''s beloved hometown football team hosted an unprecedented open tryout, the public consensus was that it was a waste of time – no one good enough to play professional football was going to be found this way.', 'tt0445990', '/gJD3VmASQCfnQTUDqvj38oe55xO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16323, 'Sister Act', False, False, True, False, 1992, 6.4, 'A Reno singer witnesses a mob murder and the cops stash her in a nunnery to protect her from the hitmen. The mother superior does not trust her, and takes steps to limit her influence on the other nuns. Eventually the singer rescues the failing choir and begins helping with community projects, which gets her an interview on TV.', 'tt0105417', '/xZvVSZ0RTxIjblLV87vs7ADM12m.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16324, 'John Carter', False, False, True, False, 2012, 6.6, 'John Carter is a war-weary, former military captain who''s inexplicably transported to the mysterious and exotic planet of Barsoom (Mars) and reluctantly becomes embroiled in an epic conflict. It''s a world on the brink of collapse, and Carter rediscovers his humanity when he realizes the survival of Barsoom and its people rests in his hands.', 'tt0401729', '/7GSSyUUgUEXm1rhmiPGSRuKoqnK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16325, 'Freaky Friday', False, False, True, False, 2003, 6.1, 'TV remake of 1976 movie Freaky Friday.', 'tt0113112', 'None');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16326, 'The Santa Clause', False, False, True, False, 1994, 6.5, 'Scott Calvin is an ordinary man, who accidentally causes Santa Claus to fall from his roof on Christmas Eve and is knocked unconscious. When he and his young son finish Santa''s trip and deliveries, they go to the North Pole, where Scott learns he must become the new Santa and convince those he loves that he is indeed, Father Christmas.', 'tt0111070', '/tBHDVtEcMl06FbCURRLGVg3TpXp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16327, 'Descendants', False, False, True, False, 2015, 6.4, 'A present-day idyllic kingdom where the benevolent teenage son of King Adam and Queen Belle offers a chance of redemption for the troublemaking offspring of Disney''s classic villains: Cruella De Vil (Carlos), Maleficent (Mal), the Evil Queen (Evvie) and Jafar (Jay).', 'tt3440298', '/65DkgHPSLVjgr6IYkpY9Aqqqid5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16328, 'Old Yeller', False, False, True, False, 1957, 7.3, 'Young Travis Coates is left to take care of the family ranch with his mother and younger brother while his father goes off on a cattle drive in the 1860s. When a yellow mongrel comes for an uninvited stay with the family, Travis reluctantly adopts the dog.', 'tt0050798', '/magA7vL3vp3ieDG3E4PMWD5b1no.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16329, 'Honey, I Shrunk the Kids', False, False, True, False, 1989, 6.3, 'The scientist father of a teenage girl and boy accidentally shrinks his and two other neighborhood teens to the size of insects. Now the teens must fight diminutive dangers as the father searches for them.', 'tt0097523', '/omQOzahi2NIeiYznNxHFDvNbvo6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16330, 'Phineas and Ferb the Movie: Across the 2nd Dimension', False, False, True, False, 2011, 7.4, 'Perry''s worst fear comes true when Phineas and Ferb finds out that he is in fact Secret Agent P, but that soon pales in comparison during a trip to the 2nd dimension where Perry finds out that Dr. Doofenshmirtz is truly evil and successful.', 'tt1825918', '/elkCKZd5aZyToTIhVWN0uUlel8y.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16331, 'Pirates of the Caribbean: On Stranger Tides', False, False, True, False, 2011, 6.6, 'Captain Jack Sparrow crosses paths with a woman from his past, and he''s not sure if it''s love -- or if she''s a ruthless con artist who''s using him to find the fabled Fountain of Youth. When she forces him aboard the Queen Anne''s Revenge, the ship of the formidable pirate Blackbeard, Jack finds himself on an unexpected adventure in which he doesn''t know who to fear more: Blackbeard or the woman from his past.', 'tt1298650', '/8fxendLObfOewRllxiM4X9Ey7S4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16332, 'The BFG', False, False, True, False, 2016, 6.4, 'The BFG is no ordinary bone-crunching giant. He is far too nice and jumbly. It''s lucky for Sophie that he is. Had she been carried off in the middle of the night by the Bloodbottler, or any of the other giants—rather than the BFG—she would have soon become breakfast. When Sophie hears that the giants are flush-bunking off to England to swollomp a few nice little chiddlers, she decides she must stop them once and for all. And the BFG is going to help her!', 'tt3691740', '/4zawxGJIH43DcoGG9Ui0WoIcrQC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16333, 'The Princess Diaries', False, False, True, False, 2001, 6.3, 'A socially awkward but very bright 15-year-old girl being raised by a single mom discovers that she is the princess of a small European country because of the recent death of her long-absent father, who, unknown to her, was the crown prince of Genovia. She must make a choice between continuing the life of a San Francisco teen or stepping up to the throne.', 'tt0247638', '/wA4lgl8gmoICSShviCkEB61nIBB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16334, 'National Treasure: Book of Secrets', False, False, True, False, 2007, 6.5, 'Benjamin Franklin Gates and Dr. Abigail Chase re-team with Riley Poole and, now armed with a stack of long-lost pages from John Wilkes Booth''s diary, Ben must follow a clue left there to prove his ancestor''s innocence in the assassination of Abraham Lincoln.', 'tt0465234', '/xxoIBbvmTj1ZttzV439jAvoovTw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16335, 'The Finest Hours', False, False, True, False, 2016, 6.8, 'The Coast Guard makes a daring rescue attempt off the coast of Cape Cod after a pair of oil tankers are destroyed during a blizzard in 1952.', 'tt2025690', '/6UG91TJ4DKI6FGdqXZfDBeRZHX8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16336, 'DuckTales: The Movie - Treasure of the Lost Lamp', False, False, True, False, 1990, 6.9, 'Scrooge McDuck, his dimwitted pilot Launch Pad, and his newphews Huey, Dewey and Louie, with Webby, arrive in Egypt where Scrooge finds the lost treasure of Collie Baba, unbeknownst to Scrooge, a magic lamp was included inside the treasure, so while the nephews have fun with the genie, they all have no idea that they''re being stalked by a power hungry sorceror named Murlock and his dimwitted thief.', 'tt0099472', '/wCvdRynGJQWaOi2qwQP5nDdFxjM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16337, 'A Goofy Movie', False, False, True, False, 1995, 6.8, 'Though Goofy always means well, his amiable cluelessness and klutzy pratfalls regularly embarrass his awkward adolescent son, Max. When Max''s lighthearted prank on his high-school principal finally gets his longtime crush, Roxanne, to notice him, he asks her on a date. Max''s trouble at school convinces Goofy that he and the boy need to bond over a cross-country fishing trip like the one he took with his dad when he was Max''s age, which throws a kink in his son''s plans to impress Roxanne.', 'tt0113198', '/bycmMhO3iIoEDzP768sUjq2RV4T.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16338, 'The Greatest Game Ever Played', False, False, True, False, 2005, 7.4, 'A biopic of 20-year-old Francis Ouimet who defeated his golfing idol and 1900 US Open Champion, Harry Vardon.', 'tt0388980', '/kh4vd6wKa6j9xVFzxhQrsT0p2YN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16339, 'The Lion King 1½', False, False, True, False, 2004, 6.5, 'Timon the meerkat and Pumbaa the warthog are best pals and the unsung heroes of the African savanna. This prequel to the smash Disney animated adventure takes you back -- way back -- before Simba''s adventure began. You''ll find out all about Timon and Pumbaa and tag along as they search for the perfect home and attempt to raise a rambunctious lion cub.', 'tt0318403', '/sVJME5R1NmTXtbdnAxYPx7Xa7kr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16341, 'The Adventures of Ichabod and Mr. Toad', False, False, True, False, 1949, 7, 'The Wind in the Willows: Concise version of Kenneth Grahame''s story of the same name. J. Thaddeus Toad, owner of Toad Hall, is prone to fads, such as the newfangled motor car. This desire for the very latest lands him in much trouble with the wrong crowd, and it is up to his friends, Mole, Rat and Badger to save him from himself. - The Legend of Sleepy Hollow: Retelling of Washington Irving''s story set in a tiny New England town. Ichabod Crane, the new schoolmaster, falls for the town beauty, Katrina Van Tassel, and the town Bully Brom Bones decides that he is a little too successful and needs "convincing" that Katrina is not for him.', 'tt0041094', '/o6GfVkgPaaHJnXNywDLpHJII2tW.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16342, 'Tinker Bell and the Lost Treasure', False, False, True, False, 2009, 6.7, 'A blue harvest moon will rise, allowing the fairies to use a precious moonstone to restore the Pixie Dust Tree, the source of all their magic. But when Tinker Bell accidentally puts all of Pixie Hollow in jeopardy, she must venture out across the sea on a secret quest to set things right.', 'tt1216516', '/6tNldk2zPUzXVKkU9aeirM9Wotu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16343, 'Bedknobs and Broomsticks', False, False, True, False, 1971, 7.1, 'Three children evacuated from London during World War II are forced to stay with an eccentric spinster (Eglantine Price). The children''s initial fears disappear when they find out she is in fact a trainee witch.', 'tt0066817', '/i4kHkhGPVzlAlr31zfhJZ747sbd.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16344, 'Hocus Pocus', False, False, True, False, 1993, 6.9, 'After 300 years of slumber, three sister witches are accidentally resurrected in Salem on Halloween night, and it us up to three kids and their newfound feline friend to put an end to the witches'' reign of terror once and for all.', 'tt0107120', '/uwGvRAHtaQIDQ0L8uMMWbxFKr7j.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16345, 'Secretariat', False, False, True, False, 2010, 7.2, 'Housewife and mother Penny Chenery agrees to take over her ailing father''s Virginia-based Meadow Stables, despite her lack of horse-racing knowledge. Against all odds, Chenery - with the help of veteran trainer Lucien Laurin - manages to navigate the male-dominated business, ultimately fostering the first Triple Crown winner in 25 years.', 'tt1028576', '/9bpAR3ALEfaqosxbi9zpuv9FH7F.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16346, 'Darby O''Gill and the Little People', False, False, True, False, 1959, 7.2, 'Take a wee bit of ancient folklore, mix in some spectacular special effects and a magical cast (including Sean Connery) -- and you''ve got one of the most enchanting fantasies of all time! A frisky old storyteller named Darby O''Gill is desperately seeking the proverbial pot of gold. There''s just one tiny thing standing in his way: a 21-inch leprechaun named King Brian. In order to get the gold, Darby must match his wits against the shrewd little trickster -- which proves no small task, indeed! Fall under the spell of DARBY O''GILL AND THE LITTLE PEOPLE for a fun-filled evening of magic, mirth, and nonstop shenanigans!', 'tt0052722', '/80DArGbj1G2kIaU1kZCR32GBoNO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16347, 'Muppet Treasure Island', False, False, True, False, 1996, 7, 'After telling the story of Flint''s last journey to young Jim Hawkins, Billy Bones has a heart attack and dies just as Jim and his friends are attacked by pirates. The gang escapes into the town where they hire out a boat and crew to find the hidden treasure, which was revealed by Bones before he died. On their voyage across the seas, they soon find out that not everyone on board can be trusted.', 'tt0117110', '/35ujbloqx6v4YtHGQIrGPbwRFML.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16348, 'The Rescuers Down Under', False, False, True, False, 1990, 6.9, 'A lawless poacher wants to capture a majestic and rare golden eagle, so he kidnaps the boy who knows where to find the bird. Not to worry -- the Rescue Aid Society''s top agents, heroic mice Miss Bianca and Bernard, fly to Australia to save the day. Accompanying the fearless duo are bumbling albatross Wilbur and local field operative Jake the Kangaroo Rat.', 'tt0100477', '/udXDHGLqxBoDcBvqExFn8rIpiZM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16349, 'The Rocketeer', False, False, True, False, 1991, 6.5, 'Straight from the pages of a pulp comic from a past era, the Rocketeer recreates 1930s Hollywood, complete with gangsters, Nazi spies, and the growth of the Age of Aviation. Young pilot Cliff Secord stumbles on a top secret rocket-pack and with the help of his mechanic/mentor, Peevy, he attempts to save his girl and stop the Nazis as The Rocketeer.', 'tt0102803', '/2tDFRESFwKww1LHRE8W1Exj9edH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16350, 'High School Musical', False, False, True, False, 2006, 5.4, 'Troy (Zac Efron), the popular captain of the basketball team, and Gabriella (Vanessa Anne Hudgens), the brainy and beautiful member of the academic club, break all the rules of East High society when they secretly audition for the leads in the school''s musical. As they reach for the stars and follow their dreams, everyone learns about acceptance, teamwork, and being yourself. And it''s all set to fun tunes and very cool dance moves!', 'tt0475293', '/lkcc7eC4qTbhE2DBnoVpGg2ZeM9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16351, 'Atlantis: The Lost Empire', False, False, True, False, 2001, 6.9, 'The world''s most highly qualified crew of archaeologists and explorers is led by historian Milo Thatch as they board the incredible 1,000-foot submarine Ulysses and head deep into the mysteries of the sea.', 'tt0230011', '/oK8yR6xSQreWf1ouP1VpXKwoop5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16352, 'The Great Muppet Caper', False, False, True, False, 1981, 7.2, 'Kermit and Fozzie are newspaper reporters sent to London to interview Lady Holiday, a wealthy fashion designer whose priceless diamond necklace is stolen. Kermit meets and falls in love with her secretary, Miss Piggy. The jewel thieves strike again, and this time frame Miss Piggy. It''s up to Kermit and Muppets to bring the real culprits to justice.', 'tt0082474', '/otcJx3KsIDR1sBOz1gryCy8joP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16353, 'Teen Beach Movie', False, False, True, False, 2013, 5.9, 'Cowabunga! Catch a perfect wave of fun in the sun, splashed with excitement, surprises and budding romance. Life''s a beach for surfers Brady (Ross Lynch) and McKenzie (Maia Mitchell) – until a rogue wave magically transports them inside the classic ''60s beach party flick, "Wet Side Story," where a full-blown rivalry between bikers and surfers threatens to erupt. There, amidst a sea of surfing, singing and dancing, Brady and Mack accidentally change the storyline, and the film’s dreamy hero and heroine fall for them instead of for each other! Can our heroes get the plot back on track, or will they be trapped there forever? Overflowing with hilarious comedy and high-energy musical numbers, "Teen Beach Movie" makes it an endless summer all year long!', 'tt2325989', '/ywX8INXLshhf94pyxc8wE2MO4aT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16354, 'Journey to the Center of the Earth', False, False, True, False, 1959, 7, 'Remake of Jules Verne''s classic story finds adventurers seeking a mysterious hidden land. They are joined by the wife of another man, who had previously gone on a similar expedition and disappeared.', 'tt0200755', '/2aGIloIUSTumRwQiJxpS4Iun1Fl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16355, 'Pollyanna', False, False, True, False, 1960, 7.4, 'Pollyanna Whittier goes to live with her wealthy but bitter aunt after the tragic death of her father. Pollyanna shares a game her father taught her -- the ''Glad Game'' -- in which everyone can find a silver lining in even the darkest cloud, and her sunny nature, good humor and determination to look on the bright side of life prove to have an astonishing effect on those around her. With the help of her orphaned friend, Jimmy Bean, she casts her spell on the grumpiest townsfolk of Beldingsville -- including the cynical shut-in Mrs. Snow, the morose millionaire Mr. Pendleton and the enigmatic Dr. Chilton. And Pollyanna masterminds the romance between her Aunt''s maid, Nancy, and the handyman, Tim. It is only Aunt Polly, who cannot bring herself to embrace Pollyanna''s innocence and joy. But all is not straightforward in Pollyanna''s war against pessimism, since she must overcome a personal tragedy that threatens to banish "glad" from her vocabulary forever.', 'tt0314519', '/72BQqQ8n1DlscEbednShXw4PNBq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16356, 'High School Musical 2', False, False, True, False, 2007, 5, 'The East High Wildcats are ready to have the time of their lives. Troy (Zac Efron) is thrilled when he’s offered a job in a country club, but it’s all part of Sharpay’s (Ashley Tisdale) plot to lure him away from Gabriella (Vanessa Hudgens). How will it all turn out? All questions are answered on the night of the club’s Talent Show.', 'tt0810900', '/la2kiVWDm2vuB4APZDgCCmuBh4K.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16357, 'Halloweentown', False, False, True, False, 1998, 6.8, 'On her 13th birthday, Marnie learns she''s a witch, discovers a secret portal, and is transported to Halloweentown — a magical place where ghosts and ghouls, witches and werewolves live apart from the human world. But she soon finds herself battling wicked warlocks, evil curses, and endless surprises.', 'tt0173886', '/2KLel13fLyOqN6NCP9N2JO8rbGn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16358, 'Brother Bear', False, False, True, False, 2003, 6.8, 'When an impulsive boy named Kenai is magically transformed into a bear, he must literally walk in another''s footsteps until he learns some valuable life lessons. His courageous and often zany journey introduces him to a forest full of wildlife, including the lovable bear cub Koda, hilarious moose Rutt and Tuke, woolly mammoths and rambunctious rams.', 'tt0328880', '/9VvLgj28J5m04y5gO0mt5np4yF8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16359, 'Millions', False, False, True, False, 2004, 6.9, 'Two boys, still grieving the death of their mother, find themselves the unwitting benefactors of a bag of bank robbery loot in the week before the United Kingdom switches its official currency to the Euro. What''s a kid to do?', 'tt0366777', '/xKbnBopSedOmsKU94OD2vsrtcnf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16360, 'Muppets Most Wanted', False, False, True, False, 2014, 6.4, 'While on a grand world tour, The Muppets find themselves wrapped into an European jewel-heist caper headed by a Kermit the Frog look-alike and his dastardly sidekick.', 'tt2281587', '/8CU1WSLAaQjLmRbEmyhfDoOJCyh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16361, 'The Journey of Natty Gann', False, False, True, False, 1985, 7.1, 'America is in the depths of the Great Depression. Families drift apart when faraway jobs beckon. In this masterful, atmospheric adventure, a courageous young girl (Meredith Salenger) confronts overwhelming odds when she embarks on a cross-country search for her father. During her extraordinary odyssey, she forms a close bond with two diverse traveling companions: a magnificent, protective wolf, and a hardened drifter (John Cusack). A brilliant, moving tapestry, woven of courage and perseverance.', 'tt0089385', '/z2eHb3ih4k9zannMKn3wmVvYAis.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16362, 'Secret of the Wings', False, False, True, False, 2012, 7.1, 'Tinkerbell wanders into the forbidden Winter woods and meets Periwinkle. Together they learn the secret of their wings and try to unite the warm fairies and the winter fairies to help Pixie Hollow.', 'tt1217213', '/iOp2tq3KkrSpVZ5DE2RFvV2OPv9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16363, 'Cars 2', False, False, True, False, 2011, 6.1, 'Star race car Lightning McQueen and his pal Mater head overseas to compete in the World Grand Prix race. But the road to the championship becomes rocky as Mater gets caught up in an intriguing adventure of his own: international espionage.', 'tt1216475', '/okIz1HyxeVOMzYwwHUjH2pHi74I.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16364, 'Tinker Bell and the Great Fairy Rescue', False, False, True, False, 2010, 6.9, 'During a summer stay on the mainland, Tinker Bell is accidentally discovered while investigating a little girl''s fairy house. As the other fairies, led by the brash Vidia, launch a daring rescue in the middle of a fierce storm, Tink develops a special bond with the lonely, little girl.', 'tt1216515', '/vuwcXForVzBgLWh6rkxip4m77gn.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16365, 'Descendants 2', False, False, True, False, 2017, 6.5, 'When the pressure to be royal becomes too much for Mal, she returns to the Isle of the Lost where her archenemy Uma, Ursula''s daughter, has taken her spot as self-proclaimed queen.', 'tt5117876', '/8BNyZlBk4Hs92z6UrvFDjvx3yMb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16366, 'Adventures in Babysitting', False, False, True, False, 2016, 6, 'Two teen rival babysitters, Jenny and Luci, team up to hunt down one of their kids who accidentally run away into the big city without any supervision.', 'tt4456850', '/xuCPo0krpAcK2s1uMCZs93turdk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16367, 'Dinosaur', False, False, True, False, 2000, 6.5, 'An orphaned dinosaur raised by lemurs joins an arduous trek to a sancturary after a meteorite shower destroys his family home.', 'tt0130623', '/rSje3FS7ycJSglowlngjsvDt7vO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16368, 'Dumbo', False, False, True, False, 2019, 6.3, 'Dumbo is a baby elephant born with oversized ears and a supreme lack of confidence. But thanks to his even more diminutive buddy -- Timothy the Mouse -- the pint-sized pachyderm learns to surmount all obstacles.', 'tt0033563', '/hKDdllslMtsU9JixAv5HR9biXlp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16369, 'The Color of Friendship', False, False, True, False, 2000, 7.2, 'Mahree Bok lives on a farm in South Africa. Her father is a policeman who cannot hide his joy when activist Steve Biko is caught by the South African authorities. Piper Dellums is the daughter of a US congressman from California and who lives in a nice home in Washington DC. When Mahree is chosen to spend a semester at the Dellums'' house, she doesn''t expect that her host family would be black. Nor do her hosts suspect that she is not a black South African.', 'tt0213565', '/5Ye77XO6qvEHal55Rfwu0SJvaBk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16370, 'Oliver & Company', False, False, True, False, 1988, 6.7, 'This animated take on Oliver Twist re-imagines Oliver as an adorable orphaned kitten who struggles to survive in New York City and falls in with a band of canine criminals led by an evil human. First, Oliver meets Dodger, a carefree mutt with street savoir faire. But when Oliver meets wealthy Jenny on one of the gang''s thieving missions, his life changes forever.', 'tt0095776', '/15CP0xItV3oIoL5VSb8hM27e9xQ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16371, 'Monkey Kingdom', False, False, True, False, 2015, 7.3, 'A newborn monkey and its mother struggle to survive within the competitive social hierarchy of the Temple Troop, a dynamic group of monkeys who live in ancient ruins found deep in the storied jungles of South Asia.', 'tt3660770', '/1kaKWGqLK8Ymmniz8pMYtd4Pyi0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16372, 'Davy Crockett, King of the Wild Frontier', False, False, True, False, 1955, 7, 'Legends (and myths) from the life of famed American frontiersman Davey Crockett are depicted in this feature film edited from television episodes. Crockett and his friend George Russell fight in the Creek Indian War. Then Crockett is elected to Congress and brings his rough-hewn ways to the House of Representatives. Finally, Crockett and Russell journey to Texas and the last stand at the Alamo.', 'tt0047977', '/vaK8YScHtsCTP9gL3BNuihv0dJF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16373, 'The Three Caballeros', False, False, True, False, 1944, 6.5, 'For Donald''s birthday he receives a box with three gifts inside. The gifts, a movie projector, a pop-up book, and a pinata, each take Donald on wild adventures through Mexico and South America.', 'tt0038166', '/nMfScRxw9wVLoO7LiEjziFAKLSK.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16374, 'That Darn Cat!', False, False, True, False, 1965, 6.8, 'A young woman suspects foul play when her cat comes home wearing a wrist watch. Convincing the FBI, though, and catching the bad guys is tougher than she imagined.', 'tt0059793', '/by92HEWg2d4WrkgbUIPEA0ufsHI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16375, 'African Cats', False, False, True, False, 2011, 7.6, 'African Cats captures the real-life love, humor and determination of the majestic kings of the savanna. The story features Mara, an endearing lion cub who strives to grow up with her mother’s strength, spirit and wisdom; Sita, a fearless cheetah and single mother of five mischievous newborns; and Fang, a proud leader of the pride who must defend his family from a once banished lion.', 'tt1223236', '/zpgNeIM7eRBJk6yJERNgYjjTiAh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16376, 'Newsies', False, False, True, False, 1992, 7, 'A week in the life of the exploited, child newspaper sellers in turn-of-the-century New York. When their publisher, Joseph Pulitzer, tries to squeeze a little more profit out of their labours, they organize a strike, only to be confronted with the Pulitzer''s hard-ball tactics.', 'tt0104990', '/gQK4OnoNbNwoJ1yfBL8jeRceKLP.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16378, 'Tuck Everlasting', False, False, True, False, 2002, 6.6, 'Natalie Babbitt''s award winning book for children comes to the screen in a lavish adaptation from Walt Disney Pictures. Winnie Foster (Alexis Bledel) is a girl in her early teens growing up in the small rural town of Winesap in 1914. Winnie''s parents (Victor Garber and Amy Irving) are loving but overprotective, and Winnie longs for a life of greater freedom and adventure.', 'tt0283084', '/9BXAC4uTHuD37hpiGOmhetqNgnO.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16379, 'The Incredible Journey', False, False, True, False, 1963, 7.1, 'The story of three pets, a cat and two dogs, who lose their owners when they are all on vacation. Can they find their way home?', 'tt0057180', '/zxYVFHsQGB2DALmIpTheXjTwFNi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16380, 'The Love Bug', False, False, True, False, 1968, 6.5, 'The film opens with narration by Jim Douglas and "memory" clips from the first film. The narration ends saying Herbie is now owned by an arrogant Scotsman named Simon Moore III (John Hannah), who abuses and insults Herbie until Herbie comes last in a race, not wanting to obey Simon. Furious, Simon sends Herbie to the junkyard. Meanwhile, Hank Cooper (Bruce Campbell), a mechanic in a small garage, is told to enter a race and persuaded to acquire Herbie. The judges are Donny Shotz (Micky Dolenz), a car enthusiast; Alex (Alexandra Wentworth), an old flame of Hank''s; and Simon. When Herbie and Hank have won the race, Alex questions Hank, and he gives her a ride, wherein Herbie takes them to an isolated road. Meanwhile, Simon finds the engineer who created Herbie: Dr. Gustav Stumpful (Harold Gould), and requests a second such car: an evil, black edition, whom Simon names Horace.', 'tt0119571', '/j4NQTMsVEHgqacmxvnenF7HX7GA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16381, 'High School Musical 3: Senior Year', False, False, True, False, 2008, 4.8, 'It''s almost graduation day for high school seniors Troy, Gabriella, Sharpay, Chad, Ryan and Taylor ― and the thought of heading off in separate directions after leaving East High has these Wildcats thinking they need to do something they''ll remember forever. Together with the rest of the Wildcats, they stage a spring musical reflecting their hopes and fears about the future and their unforgettable experiences growing up together. But with graduation approaching and college plans in question, what will become of the dreams, romances, and friendships of East High''s senior Wildcats?', 'tt0962726', '/cKWIxQh2dMUgjsxJenrf7A81D95.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16382, 'Return to Oz', False, False, True, False, 1985, 6.7, 'Dorothy, saved from a psychiatric experiment by a mysterious girl, finds herself back in the land of her dreams, and makes delightful new friends, and dangerous new enemies.', 'tt0089908', '/prd9KCTa12RDnKgyfR9hWHbMzBI.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16383, 'Blackbeard''s Ghost', False, False, True, False, 1968, 6.9, 'Peter Ustinov stars as the eponymous wraith, who returns to Earth to aid his descendant, elderly Elsa Lanchester (Stowecroft). The villains want to kick Lanchester and her friends out of their group home so that they can build a crooked casino. Good guy Steve Walker (Jones) gets caught in the middle of the squabble after evoking Blackbeard''s ghost.', 'tt0062737', '/3tEkEBNUDEJ73qQPcSZnC5OUYNj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16384, 'Chimpanzee', False, False, True, False, 2012, 7.2, 'A nature documentary centered on a family of chimps living in the Ivory Coast and Ugandan rain forests.', 'tt1222815', '/3aaXncHqPrlekc0SyY4OlJ6i3tU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16385, 'The Absent-Minded Professor', False, False, True, False, 1961, 6.8, 'Hailed as one of Walt Disney''s most hilarious comedies, The Absent-Minded Professor contains two essential elements for laughter - Fred MacMurray and Flubber, his gravity-defying formula for flying rubber!', 'tt0054594', '/6Ucn39YgU4qzeYuAPDC2gKzlA2x.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16386, 'Camp Rock', False, False, True, False, 2008, 5.2, 'When Mitchie gets a chance to attend Camp Rock, her life takes an unpredictable twist, and she learns just how important it is to be true to yourself.', 'tt1055366', '/7IXMqZnwccogptThay3togKIFWw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16388, 'Pooh''s Heffalump Movie', False, False, True, False, 2005, 6.4, 'Who or what exactly is a Heffalump? The lovable residents of the Hundred Acre Wood -- Winnie the Pooh, Rabbit, Tigger, Eeyore, Kanga and the rest of the pack -- embark on a journey of discovery in search of the elusive Heffalump. But as is always the case, this unusual road trip opens their eyes to so much more than just the creature they''re seeking.', 'tt0407121', '/e9yRYPCDIQV9npIOUUqeo1Thu4b.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16389, 'Turner & Hooch', False, False, True, False, 1989, 6.2, 'Scott Turner has 3 days left in the local police department before he moves to a bigger city to get some "real" cases, not just misdemeanors. Then Amos Reed is murdered, and Scott Turner sets himself on the case. The closest thing to a witness in the case is Amos Reed''s dog, Hooch, which Scott Turner has to take care of if it''s going to avoid being "put to sleep".', 'tt0098536', '/g3LC4mc17fQIFnPsMi81zzKC5gF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16390, 'Glory Road', False, False, True, False, 2006, 7.2, 'In 1966, Texas Western coach Don Haskins led the first all-black starting line-up for a college basketball team to the NCAA national championship.', 'tt0385726', '/bGRSV5tStxDNPRLCewnOeeiZzrY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16391, 'Princess Protection Program', False, False, True, False, 2009, 5.6, 'When Princess Rosalinda is about to become queen of her country of Costa Luna, the country is invaded by an evil dictator. She is put into the Princess Protection Program, a secret organization funded by royal families that looks after endangered princesses. Rosalinda is taken under the wing of Mason Verica, an agent in the PPP from rural Louisiana. While there, she meets his daughter,', 'tt1196339', '/aKs0ufPp0agLptnhEutDOlfwfdH.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16393, 'Greyfriars Bobby: The True Story of a Dog', False, False, True, False, 1961, 7.3, 'In Scotland 1865, An old shepherd and his little Skye terrier go to Edinburgh. But when the shepherd dies of pneumonia, the dog remains faithful to his master, refuses to be adopted by anyone, and takes to sleeping on his master''s grave in the Greyfriars kirkyard, despite a caretaker with a "no dogs" rule. And when Bobby is taken up for being unlicensed, it''s up to the children of Edinburgh and the Lord Provost to decide what''s to be done.', 'tt0054944', '/xFXjFo79fDINeakggSbUyu5yA4J.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16394, 'Diary of a Wimpy Kid', False, False, True, False, 2010, 6.2, 'Greg Heffley is headed for big things, but first he has to survive the scariest, most humiliating experience of any kid’s life – middle school! That won’t be easy, considering he’s surrounded by hairy-freckled morons, wedgie-loving bullies and a moldy slice of cheese with nuclear cooties!', 'tt1196141', '/iLAGiPnSuODt9cn332bkFElrRig.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16395, 'The Odd Life of Timothy Green', False, False, True, False, 2012, 6.6, 'A childless couple bury a box in their backyard, containing all of their wishes for an infant. Soon, a child is born, though Timothy Green is not all that he appears.', 'tt1462769', '/wKOLe7Bv9Txtm47WPmwN1iSHKHY.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16396, 'Splash', False, False, True, False, 1984, 6.2, 'A successful businessman falls in love with the girl of his dreams. There''s one big complication though; he''s fallen hook, line and sinker for a mermaid.', 'tt0088161', '/7FutTsMWBwVhjk1Ujf1wtndUVZh.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16397, 'The Princess Diaries 2: Royal Engagement', False, False, True, False, 2004, 5.8, 'Mia Thermopolis is now a college graduate and on her way to Genovia to take up her duties as princess. Her best friend Lilly also joins her for the summer. Mia continues her ''princess lessons''- riding horses side-saddle, archery, and other royal. But her complicated life is turned upside down once again when she not only learns that she is to take the crown as queen earlier than expected...', 'tt0368933', '/q4BAu7ksjFISEKMGmFjrxZK9cDg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16398, 'The Black Cauldron', False, False, True, False, 1985, 6.4, 'Taran is an assistant pigkeeper with boyish dreams of becoming a great warrior. However, he has to put the daydreaming aside when his charge, an oracular pig named Hen Wen, is kidnapped by an evil lord known as the Horned King. The villain hopes Hen will show him the way to The Black Cauldron, which has the power to create a giant army of unstoppable soldiers.', 'tt0088814', '/h64i9e6oJs2jrDZ4QzspXMqZhPF.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16399, 'The Game Plan', False, False, True, False, 2007, 6.1, 'Bachelor football star Joe Kingman seems to have it all. He is wealthy and carefree, and his team is on the way to capturing a championship. Suddenly, he is tackled by some unexpected news: He has a young daughter, the result of a last fling with his ex-wife. Joe must learn to balance his personal and professional lives with the needs of his child.', 'tt0492956', '/fcLFvIqCUrhY96mkSmr3okxaMEw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16400, 'Cinderella III: A Twist in Time', False, False, True, False, 2007, 5.9, 'The evil stepmother conjures up a spell that reverses all that the fairy godmother had done. Everything that she made (the dress, the horses and the carriage) which was how Cinderella went to "The Ball". So when all the magic is gone will there be a happily ever after all? What will Cinderella do? Find out in Cinderella III.', 'tt0465940', '/98PHWv6TTpjxvs6EHKJVyrjuuus.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16401, 'Waking Sleeping Beauty', False, False, True, False, 2009, 7.6, 'By the mid-1980s, the fabled animation studios of Walt Disney had fallen on hard times. The artists were polarized between newcomers hungry to innovate and old timers not yet ready to relinquish control. These conditions produced a series of box-office flops and pessimistic forecasts: maybe the best days of animation were over. Maybe the public didn''t care. Only a miracle or a magic spell could produce a happy ending. Waking Sleeping Beauty is no fairy tale. It''s the true story of how Disney regained its magic with a staggering output of hits - "Little Mermaid," "Beauty and the Beast ," "Aladdin," "The Lion King," and more - over a 10-year period.', 'tt1159961', '/toFCmk6AfrJcxDGuEwy6YMpUmGC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16402, 'The Tigger Movie', False, False, True, False, 2000, 6.3, 'As it happens, everybody - Pooh, Piglet, Eeyore, Roo, Rabbit, Owl - is busy preparing a suitable winter home for Eeyore. When everything they do seems to get undone by Tigger''s exuberant bouncing, Rabbit suggest Tigger go outside and find other tiggers to bounce with - a notion Tigger finds ridiculous because, after all, he''s "the onliest one" Or is he?', 'tt0220099', '/lxuiGvLHIL1ZyePP7bn6FcKj0Mr.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16403, 'Escape to Witch Mountain', False, False, True, False, 1975, 6.4, 'The tale of two orphan twins with special powers who go looking for their origins. The two are hindered by an avaricious tycoon who only wants to exploit their abilities.', 'tt0112985', '/54DODIHBZio7eTwsuprN5gSmqBT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16405, '101 Dalmatians', False, False, True, False, 1996, 5.7, 'The Live action adaptation of a Disney Classic. When a litter of dalmatian puppies are abducted by the minions of Cruella De Vil, the parents must find them before she uses them for a diabolical fashion statement.', 'tt0115433', '/8o2ADoAyG796UwTjwBFjPyBz0yG.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16406, 'The Last Song', False, False, True, False, 2010, 6, 'A drama centered on a rebellious girl who is sent to a Southern beach town for the summer to stay with her father. Through their mutual love of music, the estranged duo learn to reconnect.', 'tt1294226', '/deOzvJHnSSl8FI1HEJjPGgOsS9U.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16407, 'George of the Jungle', False, False, True, False, 1997, 5.5, 'Baby George got into a plane crash in a jungle, stayed alive and was adopted by a wise ape. Ursula Stanhope, US noble woman is saved from death on safari by grown-up George, and he takes her to jungle to live with him. He slowly learns a rules of human relationships, while Ursula''s lover Lyle is looking for her and the one who took her. After they are found, Ursula takes George to the USA.', 'tt0119190', '/3ezRDeK5RGvsq1Yui0tYVQhxDdu.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16408, 'Frank and Ollie', False, False, True, False, 1995, 7.8, 'Before computer graphics, special effects wizardry, and out-of-this world technology, the magic of animation flowed from the pencils of two of the greatest animators The Walt Disney Company ever produced -- Frank Thomas and Ollie Johnston. Frank and Ollie, the talent behind BAMBI, PINOCCHIO, LADY AND THE TRAMP, THE JUNGLE BOOK, and others, set the standard for such modern-day hits as THE LION KING. It was their creative genius that helped make Disney synonymous with brilliant animation, magnificent music, and emotional storytelling. Take a journey with these extraordinary artists as they share secrets, insights, and the inspiration behind some of the greatest animated movies the world has ever known!', 'tt0113104', '/eUIELE2IkuWkAc3JzjYz6J2Lpx5.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16409, 'The Santa Clause 2', False, False, True, False, 2002, 5.6, 'Better watch out! The big guy in red is coming to town once again. This time, Scott Calvin -- also known as Santa Claus -- finds out there''s an obscure clause in his contract requiring him to take on a wife. He has to leave the North Pole to fulfill his obligations, or else he''ll be forced to give up his Yuletide gig.', 'tt0304669', '/g8snUzCS8JhRAzAxZUCAktEGU7d.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16410, 'Heavyweights', False, False, True, False, 1995, 6.6, 'In 1952, four passionate bobsledders - Gamser, Franzl, Gustl and Leusl Peter prepare for the Winter Olympics in Oslo. There, the veteran Bavarian Gamser wants to finally put his archrival Dorfler in his place and show him his class. While the two men constantly get into your hair, the women Rosi and Anna try to calm things down. In Oslo, there is finally a big showdown, which will take place not only on a bobsled.', 'tt0780134', '/zBQVMqRlUIztSaL8HID3ksU1ua.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16411, 'Ice Princess', False, False, True, False, 2005, 6, 'With the help of her coach, her parents, and the boy who drives the Zamboni machine, nothing can stop Casey (Trachtenberg) from realizing her dream to be a champion figure skater.', 'tt0396652', '/oLWeyvu0ZJrpxDsRrzsMeaGzyZp.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16412, 'Bedtime Stories', False, False, True, False, 2008, 6, 'Skeeter Bronson is a down-on-his-luck guy who''s always telling bedtime stories to his niece and nephew. But his life is turned upside down when the fantastical stories he makes up for entertainment inexplicably turn into reality. Can a bewildered Skeeter manage his own unruly fantasies now that the outrageous characters and situations from his mind have morphed into actual people and events?', 'tt0960731', '/d0w4anZCzZX74deeQ3PE1byyg97.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16413, 'Cheaper by the Dozen', False, False, True, False, 2003, 5.9, '"Cheaper by the Dozen", based on the real-life story of the Gilbreth family, follows them from Providence, Rhode Island, to Montclair, New Jersey, and details the amusing anecdotes found in large families.', 'tt0042327', '/dDbDdSTPgeTJc3f4nJnMEX3mm8q.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16414, 'An Extremely Goofy Movie', False, False, True, False, 2000, 6.4, 'It''s all extreme sports and a life of freedom as Max sets off for college -- but Goofy misses Max so much he loses his job and goes to finish college alongside Max and his friends. But as Goofy tries to get closer to Max, both must go to the extreme to learn how to live their own lives together.', 'tt0208185', '/qr5Q3S7HC16XHQBqE4ZsJJvgUDU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16415, 'Twitches', False, False, True, False, 2005, 5.7, 'Twins separated at birth, Camryn and Alex meet by chance for the first time on their 21st birthday and discover they''re witches with the power to save their homeland of Coventry from the evil that threatens it. But when Camryn leaves Alex to face the darkness alone, will Coventry be doomed? Or will the sisters multiply their magic by standing together?', 'tt0467421', '/oQ2wWWefU2flwuPgdIkLGhPq6Tk.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16416, 'Iron Will', False, False, True, False, 1994, 6.6, 'When Will Stoneman''s father dies, he is left alone to take care of his mother and their land. Needing money to maintain it, he decides to join a cross country dogsled race. This race will require days of racing for long hours, through harsh weather and terrain. This young man will need a lot of courage and a strong will to complete this race.', 'tt0110157', '/6Ujbtp0NklUoQ67s32HyW6R5540.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16417, 'Aladdin and the King of Thieves', False, False, True, False, 1996, 6.4, 'Legendary secrets are revealed as Aladdin and his friends—Jasmine, Abu, Carpet and, of course, the always entertaining Genie—face all sorts of terrifying threats and make some exciting last-minute escapes pursuing the King Of Thieves and his villainous crew.', 'tt0115491', '/kTH7qEyvdDYMoQraQZm3LFKCge3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16418, 'Piglet''s Big Movie', False, False, True, False, 2003, 6.1, 'When the gang from the Hundred Acre Wood begin a honey harvest, young Piglet is excluded and told that he is too small to help. Feeling inferior, Piglet disappears and his pals Eeyore, Rabbit, Tigger, Roo and Winnie the Pooh must use Piglet''s scrapbook as a map to find him. In the process they discover that this very small animal has been a big hero in a lot of ways.', 'tt0323642', '/9Dlb8HCzhO6rMv9KsOwrbL5P5kl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16419, 'Because of Winn-Dixie', False, False, True, False, 2005, 6.3, 'A girl, abandoned by her mother when she was three, moves to a small town in Florida with her father. There, she adopts an orphaned dog she names Winn-Dixie. The bond between the girl and her special companion brings together the people in a small Florida town and heals her own troubled relationship with her father.', 'tt0317132', '/oMUFS7HmIyTeui5xuoIEkPYTWC8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16421, 'Mickey''s Once Upon a Christmas', False, False, True, False, 1999, 7.2, 'Mickey, Minnie, and their famous friends Goofy, Donald, Daisy and Pluto gather together to reminisce about the love, magic and surprises in three wonder-filled stories of Christmas past.', 'tt0238414', '/iBmKN7zS8fl0rhgtOr9T8BM90k8.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16422, 'Deep Blue', False, False, True, False, 2003, 7.4, 'Deep Blue is a major documentary feature film shot by the BBC Natural History Unit. An epic cinematic rollercoaster ride for all ages, Deep Blue uses amazing footage to tell us the story of our oceans and the life they support.', 'tt0365109', '/lzKirFeKspU0PktaX5BfdhSofcT.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16423, 'Red Tails', False, False, True, False, 2012, 5.9, 'The story of the Tuskegee Airmen, the first African-American pilots to fly in a combat squadron during World War II.', 'tt0485985', '/dIFWhmZMHCqPIG2hbDGt3KJyMJ1.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16425, 'Ruby Bridges', False, False, True, False, 1998, 7.2, 'The true story of Ruby Bridges, an African-American girl who, in 1960 at age 6, helped to integrate the all-white schools of New Orleans. Although she was the only black girl to come to the school she was sent to, (and since all the white mothers pulled their children out of class, she was the only one there, period), and though she faced a crowd of angry white citizens every day, she emerged unscathed, physically or emotionally. Encouraged by her teacher, a white woman from the North named Barbara Henry, and her mother, Lucille, and with her own quiet strength, she eventually broke down a century-old barrier forever, a pivotal moment in the civil-rights movement', 'tt0138068', '/kMZzzR05SvMLLxzYN0kzKNUW30h.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16426, 'Doctor Dolittle', False, False, True, False, 1998, 5.4, 'Get ready for the wildest adventure of a lifetime in the most ambitious production ever brought to film. Earning a 1967 Academy Award nomination for Best Picture, this dazzling fantasy turns both ordinary and exotic animals into talking, dancing and singing sensations! Rex Harrison is unforgettable in this inspiring adaptation of Hugh Lofting''s classic stories.Step into the English country home of the good doctor as he performs remarkable treatments on the wildest variety of patients you could imagine. Discover his secret cures and watch with wide-eyed excitement as he and his four-legged, fine-feathered friends charm their way into your heart!', 'tt0061584', '/hsbUAyzgMtF7NC6pR2SEk0UgeNU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16427, 'Brother Bear 2', False, False, True, False, 2006, 6.2, 'Kenai finds his childhood human friend Nita and the two embark on a journey to burn the amulet he gave to her before he was a bear, much to Koda''s dismay.', 'tt0465925', '/yaA8q2zHHUgFWdJNxXyAdU71SzM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16428, 'Camp Rock 2: The Final Jam', False, False, True, False, 2010, 5.2, 'Mitchie can''t wait to go back to Camp Rock and spend the summer making new music with her friends and superstar Shane Gray. But the slick new camp across the lake, Camp Star, has drummed up some serious competition — featuring newcomers Luke (Matthew "Mdot" Finley) and Dana (Chloe Bridges). In a sensational battle of the bands, with Camp Rock''s future at stake, will Camp Star''s flashy production and over-the-top antics win out, or will Camp Rockers prove that music, teamwork, and spirit are what truly matter?', 'tt1252380', '/8RSdWqC259zR72Jjo6ANAM4ndhM.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16429, 'Recess: School''s Out', False, False, True, False, 2001, 6.6, 'Recess: School''s Out is a 2001 animated film based on the Disney television series Recess. This film was produced by Walt Disney Pictures and was released theatrically nationwide on February 16, 2001.It''s the most exciting time of year at Third Street Elementary-- the end of the School Year! But boredom quickly sets in for protagonist TJ Detweiler, as his friends are headed for Summer Camp. One day, while passing by the school on his bike, he notices a green glow coming from the school''s auditorium. This is the work of the insidious ex-principal of Third Street, Phillium Benedict and his gang of ninjas and secret service look-alikes! Benedict is planning to get rid of Summer Vacation using his newly-acquired Tractor Beam, which he stole from the US Military Base in an effort to raise US Test Scores, and it''s up to the Recess Gang to stop him! In the end.', 'tt0265632', '/yLdsXdgB2VJ2uBQvYaEB5xPELlq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16430, 'The Adventures of Huck Finn', False, False, True, False, 1993, 6.2, 'Climb aboard for an extraordinary version of Mark Twain''s sweeping adventure when Walt Disney presents The Adventures of Huck Finn, starring Elijah Wood (The Lord of the Rings). Directed by Stephen Sommers (The Mummy, The Mummy Returns), it''s the unforgettable saga of a mischievous youngster and a runaway slave', 'tt0106223', '/8VpQZ43hdaTUmkMoe2CnED1Y2fS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16431, 'Lemonade Mouth', False, False, True, False, 2011, 6.9, 'The story follows five disparate high school students - Olivia, Mo, Charlie, Stella, and Wen who meet in detention. They realize they are destined to rock, and ultimately form a band that becomes a champion for students sidelined by the high school elite.', 'tt1648204', '/5Tbbxf38c16kbfezmXkPYS2tYRN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16432, 'The Crimson Wing: Mystery of the Flamingos', False, False, True, False, 2008, 7.3, 'In a remote and forgotten wilderness, one of nature''s last great mysteries unfolds: the birth, life and death of a million crimson-winged flamingos. Against a dramatic backdrop of never-before filmed landscapes, these secretive birds struggle to survive and prevail over danger and fate. This inspiring story, set in the extraordinary ''otherworld'' of Lake Natron in northern Tanzania, the cradle of humankind, reminds us: here on earth is a universe waiting to be discovered.', 'tt0902967', '/zq3vC7rimcP74BwofNJCjyNce8L.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16433, 'Big Business', False, False, True, False, 1988, 6.4, 'In the 1940s in the small town of Jupiter Hollow, two sets of identical twins are born in the same hospital on the same night. One set to a poor local family and the other to a rich family just passing through. The dizzy nurse on duty accidentally mixes the twins unbeknown to the parents. Our story flashes forward to the 1980s where the mismatched sets of twins are about to cross paths.', 'tt0094739', '/dxbXbR1P44caMUKtBdq7sX6aSgw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16434, 'Melody Time', False, False, True, False, 1948, 6.4, 'Melody Time is an animated feature produced by Walt Disney and released to theatres by RKO Radio Pictures. Made up of several sequences set to popular music and folk music, the film is, like Make Mine Music before it, the contemporary version of Fantasia.', 'tt0040580', '/aLij9PB33f4PscrJqin03TlnZtv.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16435, 'Bambi II', False, False, True, False, 2006, 6.1, 'Return to the forest and join Bambi as he reunites with his father, The Great Prince, who must now raise the young fawn on his own. But in the adventure of a lifetime, the proud parent discovers there is much he can learn from his spirited young son.', 'tt0447854', '/wIVLCnwYynKIiAheJoEQtC8gTjq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16436, 'The Cheetah Girls', False, False, True, False, 2003, 4.8, 'A four-member teen girl group named the Cheetah Girls go to a Manhattan High School for the Performing Arts and try to become the first freshmen to win the talent show in the school''s history. During the talent show auditions, they meet a big-time producer named Jackal Johnson, who tries to make the group into superstars, but the girls run into many problems.', 'tt0338852', '/2n813ySvmLdGV1CohEne7nAjUd2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16437, 'Hannah Montana: The Movie', False, False, True, False, 2009, 4.5, 'When Miley Stewart (aka pop-star Hannah Montana) gets too caught up in the superstar celebrity lifestyle, her dad decides it''s time for a total change of scenery. But sweet nibblets! Miley must trade in all the glitz and glamour of Hollywood for some ol'' blue jeans on the family farm in Tennessee, and question if she can be both Miley Stewart and Hannah Montana. With a little help from her friends – and awesome guest stars Taylor Swift, Rascal Flatts and Vanessa Williams – will she figure out whether to choose Hannah or Miley?', 'tt1114677', '/8pwbnmvshHj2yYPYlyJ8K8Yzecy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16438, 'Herbie Rides Again', False, False, True, False, 1974, 5.7, 'The living Volkswagen Beetle helps a old lady protect her home from a corrupt developer.', 'tt0071607', '/gkR1HkVe1R8VogkP8jotyS3TY33.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16441, 'The Apple Dumpling Gang', False, False, True, False, 1975, 6.5, 'A roving bachelor gets saddled with three children and a wealth of trouble when the youngsters stumble upon a huge gold nugget. They join forces with two bumbling outlaws to fend off the greedy townspeople and soon find themselves facing a surly gang of sharpshooters.', 'tt0072653', '/h7lzkNiQ7xzwkfWtuxfukIVr9U0.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16443, '101 Dalmatians II: Patch''s London Adventure', False, False, True, False, 2003, 5.8, 'Being one of 101 takes its toll on Patch, who doesn''t feel unique. When he''s accidentally left behind on moving day, he meets his idol, Thunderbolt, who enlists him on a publicity campaign.', 'tt0324941', '/mcl56J7tDPjzEesgpX4UPgAdbvf.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16444, 'Saludos Amigos', False, False, True, False, 1942, 6.2, 'Live-action segments show members of the Disney staff touring South America and recording their impressions in sketches. These segue into four animated sections: "Lake Titicaca" depicts tourist Donald Duck''s troubles with a stubborn llama; and in "Aquarela do Brasil," Jose Carioca shows Donald the sights and sound of Rio de Janeiro.', 'tt0036326', '/jiNSrKpgKdLXEUasOvNaCBgEner.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16445, 'Mighty Joe Young', False, False, True, False, 1998, 5.6, 'After being taken from his home in Africa, Gorilla "Joe" is an instant hit in a Hollywood nightclub. This fun and wonderfully entertaining slant on "King Kong" is much better than Kong''s 1934 sequel, "Son of Kong". This all ages adventure has superb special effects from Willis O''Brien and his protege, Ray Harryhausen.', 'tt0041650', '/pY9w40lQgEK4cvxbCxirvV1Wzo0.jpg');
