insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16497, 'Geek Charming', False, False, True, False, 2011, 6.4, 'Dylan Schoenfield is the pink princess of the upscale Los Angeles Woodlands Academy. She has the coolest boyfriend, the most popular friends, and a brand-new it bag that everyone covets. But when she accidentally tosses her Serge Sanchez bag into a fountain, this princess comes face-to-face with her own personal frog, self-professed film geek Josh Rosen. In return for rescuing Dylan''s bag, Josh convinces Dylan to let him film her for his documentary on high school popularity. Reluctantly, Dylan lets F-list Josh into her A-list world, and is shocked to realize that sometimes nerds can be pretty cool. But when Dylan''s so-called prince charming of a boyfriend dumps her flat, her life and her social status comes to a crashing halt. Can Dylan win the Woodlands Academy Fall Formal Blossom Queen crown? Can Josh win the Woodlands Academy 10th Annual Film Festival? Can Dylan, with Josh''s help, pull the pieces together to create her own happily-ever-after?', 'tt1835977', '/sBMF7xpd8Ms5Ngq0uVxVnx8B88L.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16498, 'Snow Dogs', False, False, True, False, 2002, 5.2, 'When a Miami dentist inherits a team of sled dogs, he''s got to learn the trade or lose his pack to a crusty mountain man.', 'tt0281373', '/rIPJRJZGleNaK1kfHBaFbGZ675m.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16499, 'Mulan II', False, False, True, False, 2004, 5.7, 'Fa Mulan gets the surprise of her young life when her love, Captain Li Shang asks for her hand in marriage. Before the two can have their happily ever after, the Emperor assigns them a secret mission, to escort three princesses to Chang''an, China. Mushu is determined to drive a wedge between the couple after he learns that he will lose his guardian job if Mulan marries into the Li family.', 'tt0279967', '/8cUIHYXpDFYX6r5ZviQuRKCVLJ2.jpg');
