insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16511, 'Teen Beach 2', False, False, True, False, 2015, 6.1, 'Dive in and rock out with the hottest surf sequel under the sun! Now that summer''s over and school has begun, Brady (Ross Lynch) and Mack''s (Maia Mitchell) relationship seems headed for a wipeout — until Lela, Tanner and the "Wet Side Story" kids show up! Dazzled by the novelty and variety of the modern world, Lela wants to stay, but the real world and the "reel" world just don''t mix. Can Mack and Brady find the magic to get the kids home and get their own romance back on track before it''s too late? Packed with electrifying song &amp; dance numbers and hilarious fish-out-of-water wackiness, "Teen Beach 2" is "wow-abunga" fun for everyone!', 'tt3764966', '/AdqZVbaUAEnmRjYr13Rmnb7ZvIS.jpg');
