insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16525, 'Jungle 2 Jungle', False, False, True, False, 1997, 5.2, 'Uptight New York City executive, Michael Cromwell, pursues his soon-to-be ex-wife to South America and returns home with the son he never knew he had -- a boy raised in a tribal village in Brazil. Armed with only his blowgun, the 13-year-old Mimi-Siku discovers that the world outside his jungle home is indeed a strange place.', 'tt0119432', '/dOTtdHNmdKaE8GrC6arlZA08o7h.jpg');
