insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16531, 'Cadet Kelly', False, False, True, False, 2002, 5.5, 'Hyperactive teenager Kelly is enrolled into a military school when her new stepfather becomes the Commandant. At first she has problems fitting in and taking orders until she tries out for the drill team.', 'tt0294425', '/lcxxJqPSTtoyvwXnr7c4Gy2Irxo.jpg');
