insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16536, 'The Reluctant Dragon', False, False, True, False, 1941, 6.9, 'Humorist Robert Benchley attempts to find Walt Disney to ask him to adapt a short story about a gentle dragon who would rather recite poetry than be ferocious. Along the way, he is given a tour of Walt Disney Studios, and learns about the animation process.', 'tt0034091', '/fNqWGKqs2kvbD6dDHxQv4Z2r7wm.jpg');
