insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16538, 'Cheetah', False, False, True, False, 1989, 6, 'An American boy and girl, spending six months in Kenya with their scientist parents adopt a cheetah, only to realize that they must set it loose so that it can learn to hunt and be free. However, when the animal is captured by poachers planning to race it against greyhounds, the two city kids, together with a young African goat herder they befriended, head off into the wild to rescue the cheetah.', 'tt0097053', '/8ZZ9HG2FqhTrqiVkT84GtlhYEXR.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16539, 'Avalon High', False, False, True, False, 2011, 5.7, 'Disney Channel''s production of Julie Sherman Wolfe''s screenplay adaptation of the popular novel Avalon High by Meg Cabot. Elaine "Ellie" Harrison has just moved from Minnesota to Annapolis, Maryland while her parents take a year long sabbatical to continue their medieval studies in nearby DC. Her new high school, Avalon High, seems like a typical high school with the stereotypical students: Lance the jock, Jennifer the cheerleader, Marco, the bad boy/desperado, and Will, the senior class president, quarterback, and all around good guy. But not everyone at Avalon High is who they appear to be, not even Ellie herself. Eventually, it becomes apparent that Avalon High is a situation where the ancient Arthurian legend is repeating itself. Will, Jennifer, Lance, Marco, and Mr. Morton all correspond to King Arthur, Queen Guinevere, Knight Lancelot, Mordred, and Merlin, respectively.', 'tt1614950', '/BhLdh2lOywpXyE61tEJ9jaJfz9.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16540, 'The Fox and the Hound 2', False, False, True, False, 2006, 5.3, 'Best friends Tod, a fox kit, and Copper, a hound puppy, visit a country fair when they see a band of dogs called "The Singin'' Strays". The band has five members: Dixie, Cash, Granny Rose, and twin brothers Waylon and Floyd. It is important that they perform well because a talent scout is visiting.', 'tt0465997', '/mIhKia4eMOBCYgHSHLHXQ01oc2A.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16541, 'Honey, We Shrunk Ourselves', False, False, True, False, 1997, 5, 'The joke''s on absent-minded scientist Wayne Szalinski when his troublesome invention shrinks him, his brother and their wives so effectively that their children think they''ve completely disappeared. Of course, this gives the kids free rein to do anything they want, unaware that their parents are watching every move.', 'tt0119310', '/wK8p7WjKw3z6nQQh9ptlMGn35DZ.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16542, 'How to Build a Better Boy', False, False, True, False, 2014, 5.5, 'Mae and Gabby are two friends who go everywhere together but they are not very popular in their school. Their classmates always pressure because they have not had a boyfriend so to avoid further setbacks, the girls put in place a plan to create the perfect boyfriend. Both believe that it will be easy to create their perfect guy using the military team building; machinery owned by the father of Mae, the machine works through a wireless keyboard. The machine can create with their settings, a robotic soldier. Of this plan was born Albert an ideal guy to be a perfect boyfriend. He will be the most popular boy of the school.', 'tt3098874', '/iQwno3tB1MFrfLAqafZ8i9Mx05v.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16543, 'The Jungle Book 2', False, False, True, False, 2003, 5.4, 'Mowgli, missing the jungle and his old friends, runs away from the man village unaware of the danger he''s in by going back to the wild.', 'tt0283426', '/DhuskQc9lSjPiYKr9hy0AZ7uiC.jpg');
