insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16545, 'Tin Toy', False, False, True, False, 1988, 6.5, 'Babies are hardly monster-like, unless you''re a toy. After escaping a drooling baby, Tinny realizes that he wants to be played with after all. But in the amount of time it takes him to discover this, the baby''s attention moves on to other things only an infant could find interesting.', 'tt0096273', '/bNoX2MCpquxk60skGCLignnRqpy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16546, 'Return to Halloweentown', False, False, True, False, 2006, 5.6, 'As Halloweentown prepares to celebrate its 1,000th anniversary, Marnie Piper and her brother Dylan return to Witch University, where trouble is in session from the Sinister Sisters and from someone who''s plotting to use Marnie''s powers for evil.', 'tt0816520', '/yYIVsEUyGdp8bEtRjOZGwQA3qmq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16548, 'Johnny Tsunami', False, False, True, False, 1999, 6.3, 'A Hawaiian teenage surfer shows off his skills when he takes to the snow slopes in Vermont.', 'tt0206064', '/d2jnJHWVJc8D5ms1xhJ2yRkyV3U.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16550, 'Don''t Look Under the Bed', False, False, True, False, 1999, 6.9, 'A girl calls on her brother''s imaginary friend to banish a mischievous boogeyman who has framed her for his pranks.', 'tt0208101', '/zVK76DDtIKdIiRRraAVrsyek04t.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16551, 'I''ll Be Home for Christmas', False, False, True, False, 1998, 5.5, 'Estranged from his father, college student Jake is lured home to New York for Christmas with the promise of receiving a classic Porsche as a gift. When the bullying football team dumps him in the desert in a Santa suit, Jake is left without identification or money to help him make the journey. Meanwhile, his girlfriend, Allie, does not know where he is, and accepts a cross-country ride from Jake''s rival, Eddie.', 'tt0155753', '/vzxvMRJtXfqdiwBYzDJXDSIOYWi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16552, 'Invisible Sister', False, False, True, False, 2015, 6.1, 'Teenager Cleo''s school science project goes quite awry, causing her popular older sister Molly to go invisible.', 'tt4420110', '/seWQvVIzb5xBjHW0Y1h4jvokxZN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16553, 'Confessions of a Teenage Drama Queen', False, False, True, False, 2004, 4.6, 'When the teenager Mary Elizabeth Steppe, a.k.a. Lola, moves with her mother and two younger twin sisters from New York to the suburb of Dellwood, New Jersey, she has the feeling that her cultural and entertaining world ended. While in school, the displaced Lola becomes close friend of the unpopular Ella, who is also a great fan of the her favorite rock band Sidarthur. However, the most popular girl in the school, Carla Santini, disputes the lead role in an adaptation of Pygmalion with Lola and also the leadership of their mates. When the last concert of Sidarthur is sold-out, Lola plans with Ella to travel to New York and buy the tickets from scalpers. However, the girls get into trouble while helping the lead singer and Lola''s idol Stu Wolf, changing their lives forever.', 'tt0361467', '/ahV4Lt24bogI8GB08ztBS9muomi.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16554, 'The Shaggy D.A.', False, False, True, False, 1976, 5.9, 'Wilby Daniels, a successful lawyer running for District Attorney, suddenly finds himself being transformed into an English sheepdog. Somehow he has to keep his change a secret and find just what is causing it, all the while eluding the local dog catcher.', 'tt0075200', '/cSVu5gGSelx2QOykeEG5lEuzD1L.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16555, 'Minutemen', False, False, True, False, 2008, 5.8, 'A comedy/sci-fi/adventure about three high school kids who invent a time machine to spare others just like them from the humiliation they''ve endured.', 'tt1072755', '/2y00QZiT3XUGwIxQxY5WLvvd1Y6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16556, 'Shipwrecked', False, False, True, False, 1990, 6.7, 'A young Norwegian boy in 1850s England goes to work as a cabin boy and discovers some of his shipmates are actually pirates.', 'tt0099816', '/384TfqfGu6SNurZ9extKH1UT26J.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16559, '12 Dates of Christmas', False, False, True, False, 2011, 6.3, '12 Dates of Christmas is a romantic comedy that follows Kate (Amy Smart), a young woman who re-lives the same first date on Christmas Eve over and over again. In an attempt to win back her ex-boyfriend on Christmas Eve, Kate ends up ruining her blind date with Miles (Mark-Paul Gosselaar), a handsome guy she''s been set up with. In a strange twist of fate, Kate is given the chance to re-live Christmas Eve twelve times!  Kate decides to embrace what Christmas Eve has given her -- a loving family, great friends and Miles, who turns out to be a great guy! However, just as she seems to think she has gotten it right, the clock strikes midnight and Kate must live Christmas Eve once again. Under these weird new circumstances, Kate learns to take risks and enjoy what she''s been given. Thanks to the gift of Christmas Eve, Kate finds happiness by finally trusting fate.', 'tt1846442', '/26X4Lldt6bwLnrX6kkLfUZTLojw.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16560, 'Cow Belles', False, False, True, False, 2006, 5.2, 'In order to learn how to be responsible, two wealthy teen sisters are forced to work in the family business by their exasperated father. When company funds goes missing, it''s up to the girls to save the day.', 'tt0489007', '/7I3VMLPDxL2O3pBvGLy77SFTeER.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16561, 'The Brave Little Toaster to the Rescue', False, False, True, False, 1997, 6.3, 'Those fun-loving eletrical appliances from the acclaimed animated hit ''The Brave Little Toaster'' are back in an action-packed adventure with four all-new songs. This heartfelt and humorous full-length feature reunites Toaster, Blanky, Lampy, Radio and Kirby the vacuum cleaner--the beloved household gadgets of college student Rob. When Toaster and the gang spark freindships with the playful animals at the veterinary hospital, they soon discover their new pals are about to be sent to a testing laboratory. Through teamwork (and combined voltage), they embark on a hilarious rescue to save all the animals, including Sebastion, a wise monkey, and Maisie, the doting mother cat with kittens.Treat your entire family to ''The Brave Little Toaster To The Rescue'', an imaginative film bursting with colorful animation, high-energy music and characters that''ll warm your heart at the push of a button!', 'tt0163986', '/zCJEMzVnxQC6fdxY9W3eDf3EFfl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16562, 'Blank Check', False, False, True, False, 1994, 5.3, 'Bullied by his siblings and nagged by his parents, 11-year-old Preston is fed up with his family -- especially their frugality. But he gets his chance to teach them a lesson when a money-laundering criminal nearly bulldozes Preston with his car and gives the boy a blank check as compensation. Preston makes the check out for $1 million and goes on a spending spree he''ll never forget. Maybe now, his family will take him seriously!', 'tt0109287', '/wmr5yiWbfM1LlOpkNTQ1pHDipUE.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16563, 'Cinderella II: Dreams Come True', False, False, True, False, 2002, 5, 'As a newly crowned princess, Cinderella quickly learns that life at the Palace - and her royal responsibilities - are more challenging than she had imagined. In three heartwarming tales, Cinderella calls on her animal friends and her Fairy Godmother to help as she brings her own grace and charm to her regal role and discovers that being true to yourself is the best way to make your dreams come true.', 'tt0291082', '/gmyZ10NuMrQ5FZHXsqVpRQJ5JPy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16564, 'The Thirteenth Year', False, False, True, False, 1999, 6, 'A teen learns that his birth mother is a mermaid after he begins to grow fins and slimy scales on his thirteenth birthday.', 'tt0200208', '/lx5qYxPn1fgxtYSWt7jpxT7WCJU.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16567, 'Davy Crockett and the River Pirates', False, False, True, False, 1956, 6.8, 'Davy Crockett and his sidekick Georgie compete against boastful Mike Fink ("King of the River") in a boat race to New Orleans. Later, Davy and Georgie, allied with Fink, battle a group of river pirates trying to pass themselves off as Native Americans. Written by Ken Miller', 'tt0049125', '/obMOXFtwiySiaQhWZZgE3UWsygB.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16570, 'Tarzan & Jane', False, False, True, False, 2002, 5.4, 'Sprightly amusement for kids of all ages, Disney''s direct-to-video sequel to its animated hit sandwiches jungle derring-do and lots of silly sight gags between song and dance number. With the first anniversary of her wedding to Tarzan (voice of Michael T. Weiss) beckoning, Jane (Olivia D''Abo) ponders how to make it the perfect English celebration', 'tt0313680', '/esRxshmD2BY1UPkiUHt7Gn2mksl.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16571, 'Bad Hair Day', False, False, True, False, 2015, 6, 'The film is about a high school tech whiz (Laura Marano), who is determined to become prom queen. But on the big day, she suddenly wakes up having a bad hair day, and her destroyed prom dress, and everything that can go wrong, does go badly wrong. A police officer (Leigh-Allyn Baker) seeks the necklace that the teen somehow ends up possessing. Prom day goes really bad as the pair is pursued by a dogged jewel thief (Christian Campbell) on a wild ride cross around the city.', 'tt3856042', '/rcvszjFjH8Wm4QBnU4xRUizxACg.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16572, 'Operation Dumbo Drop', False, False, True, False, 1995, 5.1, 'Five Green Berets stationed in Vietnam in 1968 grudgingly undertake the mission of a lifetime -- to secretly transport an 8,000-pound elephant through 200 miles of rough jungle terrain. High jinks prevail when Capt. Sam Cahill promises the Montagnard villagers of Dak Nhe that he''ll replace their prized elephant in time for an important ritual. But for Capt. T.C. Doyle, the mission becomes a jumbo-sized headache!', 'tt0114048', '/it01YaeL2NsHsLCddmjbz8VGabo.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16573, 'The Story of Robin Hood and His Merrie Men', False, False, True, False, 1952, 6.7, 'Young Robin Hood, in love with Maid Marian, enters an archery contest with his father at the King''s palace. On the way home his father is murdered by henchmen of Prince John. Robin takes up the life of an outlaw, gathering together his band of merry men with him in Sherwood Forest, to avenge his father''s death and to help the people of the land that Prince John are over taxing.', 'tt0045197', '/zF0ML0aWxHrYsmwMKEjNK35orIC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16574, 'Read It and Weep', False, False, True, False, 2006, 5.5, 'A young girl turns into an A-List celebrity over night when her private journal is accidently published and becomes a best-seller.', 'tt0494716', '/yDEjMLCC4n9qtEdRSyemVNpNO3r.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16575, 'The Living Desert', False, False, True, False, 1953, 7.5, 'The Living Desert was the first feature-length film in Disney’s True-Life Adventures series of documentaries focusing on zoological studies; the previous films in the series, including the Academy Award-winning Seal Island, were short subjects.  The documentary was filmed at the Westward Look Wyndham Grand Resort and Spa in Tucson, Arizona. Most of the wildlife shown in the film was donated to what would soon become the Arizona-Sonora Desert Museum.  The film was inspired by 10 minutes of footage shot by N. Paul Kenworthy Jr., a doctoral student at the University of California at Los Angeles. Kenworthy’s footage of a battle between a tarantula and a wasp intrigued Disney, who funded a feature-length production following the lives of diverse desert species. Disney was highly supportive of Kenworthy’s work and its impact on nonfiction filmmaking, stating, “This is where we can tell a real, sustained story for the first time in these nature pictures.”', 'tt0046008', '/reZht22XbHyV8eBcbpBz5on3caa.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16576, 'Herbie Goes Bananas', False, False, True, False, 1980, 5, 'The adorable little VW helps its owners break up a counterfeiting ring in Mexico.', 'tt0080861', '/bjTA6ARhXzPUZTWMg7ScwVlu4w4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16578, 'Double Teamed', False, False, True, False, 2002, 6.1, 'Heather and Heidi play volleyball for their school team, when their family decides that they need to go to a bigger school so that they will have a better chance for scholarships. But at the new school, the twins are discovered by the basketball coach. Inspired by the true story of Heather and Heidi Burge.', 'tt0281905', '/7ViRfzyMxvx22nwqPpT19obAuW3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16579, 'The Brave Little Toaster Goes to Mars', False, False, True, False, 1998, 5.7, 'Based on the book by Thomas M. Disch and intended as the third film in the series, this sequel was finished and released prior to ''The Brave Little Toaster To The Rescue''. Whilst trying to protect their new "Little Master" the anthropomorphic appliances set off on an epic adventure and make many new friends along the way.', 'tt0147926', '/zO6SF0qLZfgSVRIfk8Y8l4hmN5e.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16580, 'First Kid', False, False, True, False, 1996, 5.2, 'Some say that to be the leader of a country is one of the loneliest jobs in the world. But being the child of a world leader can be doubly so. Constantly surrounded by security officers, restricted in movements and having almost every waking moment carefully monitored makes normalcy an impossibility. No one knows this better than young Luke Davenport, the son of U.S. President Davenport. He vents his loneliness, frustration and feelings of isolation from family and friends by being a brat to his private Secret Service agent. When the agent snaps from the strain in front of the First Lady, a new agent is assigned to Luke. He turns out to be the enormous Sam Simms, a bit of a rogue who managed to rise through the ranks by sheer determination rather than strict adherence to Secret-Service protocol. At first, Luke tries all his old tricks upon Sam. But instead of getting angry, Sam seems to actually understand.', 'tt0116311', '/yykc14pfZbtDMSPRkFXgZ1Lla8I.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16584, 'Go Figure', False, False, True, False, 2005, 5.7, 'Katelin Kingsford, an Olympic hopeful figure skater wants to train with a Russian figure skating coach. In order to go to go to the school that Natasha is at, Katelin gets a scholarship playing on the girls hockey team.', 'tt0447987', '/qXKLoiRh0r8eI6MfB4cGLwy9LYS.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16585, 'Stuck in the Suburbs', False, False, True, False, 2004, 5.6, 'Brittany Aarons  is one of the many girls who has a crush on popular singer and boy-toy Jordan Cahill. However, she is bored of living a suburban existence and seeks a little something more.', 'tt0410696', '/5pbMhLMKqTLw5rrZMa1nmqFtBQ4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16586, 'Ice Age: The Great Egg-Scapade', False, False, True, False, 2016, 5.9, 'A harried prehistoric bird mom Ethel entrusts her precious, soon-to-hatch egg to Sid. When she recommends him to her neighbors – a condor mom, Cholly Bear and Gladys Glypto – business booms at his new egg-sitting service.  However, dastardly pirate bunny Squint, who is seeking revenge on the herd, steals, camouflages and hides all the eggs. Once again, with Squint’s twin brother, Clint, assisting, Manny, Diego and the rest of the gang come to the rescue and take off on a daring mission that turns into the world’s first Easter egg hunt.', 'tt5480340', '/ysQqYp9G37Q114Nc6uvCHbDmaj6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16587, 'College Road Trip', False, False, True, False, 2008, 4.3, 'When an overachieving high school student decides to travel around the country to choose the perfect college, her overprotective cop father also decides to accompany her in order to keep her on the straight and narrow.', 'tt0997047', '/x5pw0xMuQkfs0pu5PkUeVL5gd6u.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16588, 'Girl vs. Monster', False, False, True, False, 2012, 5.5, 'Skylar finds out that her parents are monster hunters after she accidentally releases some monsters from a secret containment chamber; so she and her techno friends must recapture all the monsters and also save her mom and dad from these monsters who are out for revenge.', 'tt2326087', '/loYCEzuOt70tTgd1JhBfIfIBbHb.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16590, 'Right on Track', False, False, True, False, 2003, 6.2, 'Based a on a true story about two sisters who came out on top of a man''s sport. The story is based on Erica and Courtney Enders, two sisters who get in to junior drag racing and make it all the way to the top. The two sisters fight a battle of fellow racers who are against having girls race with them therefore it pushes them harder to compete against their competition. Erica becomes stressed when her racing life becomes mixed with her social life and academic goals, and decided to quit racing, until she realizes racing is what she truly wants to do. Finally towards the end of their teen years the Enders sisters come out on top to win the junior drag racing national title. They continue to race throughout high school and college, and still do so today.', 'tt0323810', '/lUisDBAV9R552TRM0xZM1TEy6gy.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16592, 'Wendy Wu: Homecoming Warrior', False, False, True, False, 2006, 5.4, 'It is the story of an average, popular American teenager named Wendy Wu who discovers that in order to win the coveted crown she must first learn the way of the warrior. Wendy Wu has a one track mind, and that track leads directly to the title of homecoming queen -- no unscheduled stops, and no unnecessary detours. When a mysterious Chinese monk named Shen arrives to mold Wendy into a fearless kung fu warrior, however, her royal aspirations suddenly jump the track as she desperately attempts to juggle her boyfriend, her homework, and of course, the fierce competition to become homecoming queen. Now, as Wendy begins to train her mind, body, and spirit in the ancient tradition of the martial arts and her inner warrior gradually begins to emerge, the girl who once obsessed over popularity finally begins to put that popularity into perspective as she gradually realizes what truly matters in life.', 'tt0790781', '/70i9WopFTHmTJbKbKQGdYWBHnj6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16593, 'Marvel: 75 Years, From Pulp to Pop!', False, False, True, False, 2014, 7.3, 'In celebration of the publisher''s 75th anniversary, the hour-long special will take a detailed look at the company''s journey from fledgling comics publisher to multi-media juggernaut. Hosted by Emily VanCamp (S.H.I.E.L.D. Agent Sharon Carter), the documentary-style feature will include interviews with comic book icons, pop culture authorities, and Hollywood stars.  The special also promises an "extraordinary peek into Marvel''s future!" Might Marvel release the first official footage from next year''s Avengers: Age of Ultron or Ant-Man? If they do, you''ll know about it here.', 'tt4128102', '/qNC8co8LuGBv22Fu9SC71ppAwoA.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16595, 'Phantom of the Megaplex', False, False, True, False, 2000, 6.5, 'A young man working at a cinema on a special premiere night, finds that the films being shown are full of strange and eerie occurrences.', 'tt0266850', '/vr16a49cYwDlmKzkxJug0jUrjgj.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16596, 'The Apple Dumpling Gang Rides Again', False, False, True, False, 1979, 6.2, 'Amos and Theodore, the two bumbling outlaw wannabes from The Apple Dumpling Gang, are back and trying to make it on their own. This time, the crazy duo gets involved in an army supply theft case -- and, of course, gets in lots of comic trouble along the way!', 'tt0078790', '/wqvmT1BfNwaz9ivCZnzXLdRPvf2.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16598, 'Atlantis: Milo''s Return', False, False, True, False, 2003, 5.1, 'Three different stories come to life in this sequel to the hit animated Disney adventure. Explorer Milo Thatch, his new sweetheart, Queen Kida, and the rest of the team are preparing to rebuild the underwater city. But trouble crops up. Harnessing the power of the crystal of Atlantis, the adventurers set out to defend their kingdom against dark forces that threaten from sand, sea and snow.', 'tt0344864', '/c9siOypFgzsKL4LQI1d3EkDN6U3.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16599, 'Snowball Express', False, False, True, False, 1972, 6.5, 'When John Baxter inherits a ski resort in the Rocky Mountains, he quits his job in New York and moves the family west to run it. Only to find that the place is a wreck. But together they decide to try to fix it up and run it. But Martin Ridgeway, who wants the property, does everything he can to ensure it will fail.', 'tt0069291', '/d9Sn3uuT6tYJo4RscvNqddqikgC.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16601, 'The Hunchback of Notre Dame II', False, False, True, False, 2002, 4.7, 'Now that Frollo is gone, Quasimodo rings the bell with the help of his new friend and Esmeralda''s and Phoebus'' little son, Zephyr. But when Quasi stops by a traveling circus owned by evil magician Sarousch, he falls for Madellaine, Sarouch''s assistant.', 'tt0257778', '/vbwmvaEPg4m2iS7IGwXuvUVAbns.jpg');
