insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16617, 'Eddie''s Million Dollar Cook Off', False, False, True, False, 2003, 6, 'Eddie Ogden is his pa''s pride and joy as well as the Groundhogs team''s only asset as baseball talent. Then Eddie discovers a taste and talent for cuisine. Although his brothers Andy and Alex, and Pa as well as classmates enjoy his dishes, they only mock cooking, so he arranges and ''accidental'' registration for him and two friends in Home Economics. Only Eddie -secretly again- and nerdy shrew Bridget Simons enter a national cooking competition for school-kids. Ma finds out and to his surprise proves supportive, as well as the teacher, who once won the competition herself.', 'tt0365149', '/xuUVN0s7IFXoXJW9xCzpDd4mKL6.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16618, 'Babes in the Woods', False, False, True, False, 1932, 6.8, 'A loose retelling of Hansel and Gretel: two children, lost in the woods, stumble upon a dwarf village. They''re lured into a candy house by a Witch with horror-fying results.', 'tt0022647', '/lIdYRKdDQrHmGR13L9ZmNICBmNq.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16619, 'America''s Heart and Soul', False, False, True, False, 2004, 5.1, 'Filmmaker Louis Schwartzberg hits the road to capture America''s people and its natural beauty. sea to shining sea, from amber waves of grain to purple mountain majesties, it''s not merely the land that makes America beautiful -- it''s her people. Captured with stunning cinematography, AMERICA''S HEART &amp; SOUL takes you on a journey that weaves across this great nation, revealing a rich tapestry of ordinary people living extraordinary lives as they follow their dreams with the freedom of spirit that''s uniquely American. From the Vermont dairy farmer, to the blind mountain climber, to the father and son marathon runners, their inspiring stories are as different as can be -- passionate, colorful, courageous, funny, touching.', 'tt0381006', '/gQXYEn2nCvp0jbc9ZbXu41RWSYN.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16621, 'A Ring of Endless Light', False, False, True, False, 2002, 6.1, 'When 15-year-old Vicky Austin, her sister Suzy and little brother Rob visit their grandfather on Seven Bay Island, Vicky faces several unexpected challenges. Her beloved grandfather, retired Reverend Eaton, seems to be seriously ill, but tries to pretend that nothing is wrong. Vicky met the rich but emotionally troubled Zachary Gray the previous summer, and he reappears to renew the acquaintance. Another boy, 17-year-old Adam Eddington, recruits Vicky to help him with a research project, working with a dolphin called Basil. Vicky discovers she can communicate telepathically with the dolphin and his mate - and possibly with Adam as well', 'tt0282123', '/pAualVXEZh1BTRWgbLQ59JCQ6f4.jpg');
insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16622, 'The Other Me', False, False, True, False, 2000, 6.1, 'Will Browning accidently creates a clone of himself when working on a science project.', 'tt0246834', '/dA262duALaMG8CJcwnDZWQOa4RP.jpg');
