insert into movies (mid, name, on_netflix, on_prime, on_disney, on_hulu, year, rating, overview, imdb_id, poster_path) values (16708, 'The Biscuit Eater', False, False, True, False, 1972, 5.7, 'Nothing warms the heart like the story of a boy and his dog. Lonnie (Johnny Whitaker) and Text (George Spell) are two friends determined, against all odds, to turn a misfit hound into a hero. Tennessee farmer and dog trainer Harve McNeil (Earl Holliman) tells his son Lonnie that his dog, Moreover, is a good-for-nothing "biscuit eater."', 'tt0068276', '/ej4XBo3Hvdm8fVSaPhp8DVDf0S6.jpg');
